INSERT INTO `article` VALUES (106, 60, 'Cpp', '差分矩阵', '<pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">#include&nbsp;&lt;iostream&gt;#include&nbsp;&lt;cstdio&gt;using&nbsp;namespace&nbsp;std;const&nbsp;int&nbsp;N&nbsp;=&nbsp;1e3&nbsp;+&nbsp;10;int&nbsp;a[N][N],&nbsp;b[N][N];//&quot;差分操作&quot;void&nbsp;insert(int&nbsp;x1,&nbsp;int&nbsp;y1,&nbsp;int&nbsp;x2,&nbsp;int&nbsp;y2,&nbsp;int&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;b[x1][y1]&nbsp;+=&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;b[x2&nbsp;+&nbsp;1][y1]&nbsp;-=&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;b[x1][y2&nbsp;+&nbsp;1]&nbsp;-=&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;b[x2&nbsp;+&nbsp;1][y2&nbsp;+&nbsp;1]&nbsp;+=&nbsp;c;}int&nbsp;main(){\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;n,&nbsp;m,&nbsp;q;\n&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;n&nbsp;&gt;&gt;&nbsp;m&nbsp;&gt;&gt;&nbsp;q;\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=n;&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;j&nbsp;=&nbsp;1;&nbsp;j&nbsp;&lt;=&nbsp;m;&nbsp;j&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;a[i][j];\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=&nbsp;n;&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;j&nbsp;=1;&nbsp;j&nbsp;&lt;=&nbsp;m;&nbsp;j&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;insert(i,&nbsp;j,&nbsp;i,&nbsp;j,&nbsp;a[i][j]);&nbsp;&nbsp;//构建差分数组\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(q&nbsp;--)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;x1,&nbsp;y1,&nbsp;x2,&nbsp;y2,&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;x1&nbsp;&gt;&gt;&nbsp;y1&nbsp;&gt;&gt;&nbsp;x2&nbsp;&gt;&gt;&nbsp;y2&nbsp;&gt;&gt;&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;insert(x1,&nbsp;y1,&nbsp;x2,&nbsp;y2,&nbsp;c);\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=&nbsp;n;&nbsp;i&nbsp;++)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;j&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=&nbsp;m;&nbsp;j&nbsp;++)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a[i][j]&nbsp;=&nbsp;a[i&nbsp;-&nbsp;1][j]&nbsp;+&nbsp;a[i][j&nbsp;-&nbsp;1]&nbsp;-&nbsp;a[i&nbsp;-&nbsp;1][j&nbsp;-1]&nbsp;+&nbsp;b[i][j]&nbsp;//&nbsp;二维前缀和\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=&nbsp;n;&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;j&nbsp;=&nbsp;1;&nbsp;i&nbsp;&lt;=&nbsp;m;&nbsp;j&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(&quot;%d&quot;,&nbsp;a[i][j]);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n		puts(&quot;&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;}</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">差分这样构建的原因：首先假设a,b都为空数组，也就是全部为0的情况，那么我们也可以说，b数组是a数组的差分数组！因为a[i][j] = b[1][1]+…b[i][j],因为都是0，所有情况满足！在这时a数组其实不为空，我们把a数组中的值看作（0+a[i][j]）那么a数组的值变了，变成了a[i][j],这时如果b数组要想成为a数组的差分数组，值也要变为a[i][j],所以我们把b[i][j]变为a[i][j]，这样就能满足b数组是a数组的差分数组。也就相当于想b数组中插入a[i][j].</p></blockquote><p><br/></p>', '河南', '2022-06-07 00:03:02', 0, 0, 1, 1);
INSERT INTO `article` VALUES (107, 60, 'Cpp', '双指针算法', '<blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">归并排序 两个指针指向两个序列</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">快速排序 两个指针指向一个序列的首位 维护一个区间</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">大部分写法</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">for(i&nbsp;=&nbsp;0,&nbsp;j&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;n;&nbsp;i&nbsp;++){\n&nbsp;&nbsp;&nbsp;&nbsp;while(j&nbsp;&lt;&nbsp;i&nbsp;&amp;&amp;&nbsp;check(i,j))&nbsp;j&nbsp;++;\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;//每道题目的具体逻辑}</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">核心思想：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">将暴力算法O(n^2)优化到O(n)</p></blockquote><hr/><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">ex1：输入一个字符串，把其中的每一个单词输出出来，单词间空格隔开，每个单词隔一行</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">#include&nbsp;&lt;iostream&gt;#include&nbsp;&lt;string.h&gt;using&nbsp;namespace&nbsp;std;int&nbsp;main(){\n&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;str[1000];\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;gets(str);\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;n&nbsp;=&nbsp;strlen(str);\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;0;&nbsp;str[i];&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;j&nbsp;=&nbsp;i;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while(j&nbsp;&lt;&nbsp;n&nbsp;&amp;&amp;&nbsp;str[j]&nbsp;!=&nbsp;&#39;&nbsp;&#39;)&nbsp;j&nbsp;++&nbsp;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;这道问题的具体逻辑\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;k&nbsp;=&nbsp;i;&nbsp;k&nbsp;&lt;&nbsp;j;&nbsp;k&nbsp;++&nbsp;)&nbsp;cout&nbsp;&lt;&lt;&nbsp;str[k];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i&nbsp;=&nbsp;j;\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">ex2：给定一个长度为n的整数序列，请找出最长的不包含重复数字的连续子序列，输出它的长度。</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">//朴素做法:&nbsp;O(n^2)for(int&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;n;&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;for(int&nbsp;j&nbsp;=&nbsp;0;&nbsp;j&nbsp;&lt;=&nbsp;i;&nbsp;j&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(check(j,&nbsp;i))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;res&nbsp;=&nbsp;max(res,&nbsp;i&nbsp;-&nbsp;j&nbsp;+&nbsp;1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</pre><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">//双指针算法优化：O(n)for&nbsp;(int&nbsp;i&nbsp;=&nbsp;0,&nbsp;j&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;n;&nbsp;i&nbsp;++&nbsp;){\n&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(j&nbsp;&lt;=&nbsp;i&nbsp;&amp;&amp;&nbsp;check(j,i))&nbsp;j&nbsp;++;&nbsp;//判断是否有重复元素\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;res&nbsp;=&nbsp;max(res,&nbsp;i&nbsp;-&nbsp;j&nbsp;+&nbsp;1);}</pre><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">//题解#include&nbsp;&lt;iostream&gt;using&nbsp;namespace&nbsp;std;const&nbsp;int&nbsp;N&nbsp;=&nbsp;1e5&nbsp;+&nbsp;10;int&nbsp;n;int&nbsp;a[N],&nbsp;s[N];int&nbsp;main(){\n&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;n;\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;n;&nbsp;i&nbsp;++&nbsp;)&nbsp;cin&nbsp;&gt;&gt;&nbsp;a[i];\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;res&nbsp;=&nbsp;0;\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(int&nbsp;i&nbsp;=&nbsp;0,&nbsp;j&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;n;&nbsp;i&nbsp;++&nbsp;)\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;s[a[i]]&nbsp;++;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(s[a[i]]&nbsp;&gt;&nbsp;1)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;s[a[j]]&nbsp;--&nbsp;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;j&nbsp;++;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;res&nbsp;=&nbsp;max(res,&nbsp;i&nbsp;-&nbsp;j&nbsp;+&nbsp;1);\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;res&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;}</pre><p><br/></p>', '河南', '2022-06-07 00:03:27', 0, 0, 0, 1);
INSERT INTO `article` VALUES (108, 60, 'Java', 'JAVAse基础', '<blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">Java语言环境的关系(JDK、JRE、JVM关系)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">JDK = JRE + 开发工具集(例如Javac编译工具等)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">JRE = JVM + Java SE标准类库</p></blockquote><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>HelloWorld程序</h2><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;HelloChina&nbsp;{\n\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args){\n		System.out.println(&quot;Hello&nbsp;World!&quot;);\n	}\n\n}</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">使用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">javac HelloWorld.java</code>编译，使用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">java HelloChina</code>执行字节码文件</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">public后的类名和文件名必须一致</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">在一个java源文件中可以声明多个class。但是，只能最多有一个类声明为public的。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">而且要求声明为public的类的类名必须与源文件名相同</span></p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">程序的入口是main()方法，格式是固定的。</p></blockquote><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关键字与保留字</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义：被Java语言赋予了特殊含义,用做专门用途的字符串(单词)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">特点：关键字中所有字母都为小写</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>标识符</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">Java对各种变量、方法和类等要素命名时使用的字符序列称为标识符</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">技巧：凡是自己可以起名字的地方都叫标识符</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义合法标识符规则：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">由26个英文字母大小写，0-9 ，_或&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">$</span>&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">组成</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">数字不可以开头。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">不可以使用关键字和保留字，但能包含关键字和保留字。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">Java中严格区分大小写，长度无限制。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">标识符不能包含空格。</span></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>Java中的名称命名规范</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">包名：多单词组成时所有字母都小写: xxxyyyzzz</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">类名、接口名：多单词组成时，所有单词的首字母大写：XxxYyyZzz(驼峰式)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">变量名、方法名：多单词组成时，第一个单词首字母小写，第二个单词开始每个单词首字母大写：xxxYyyZzz</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">常量名：所有字母都大写。多单词时每个单词用下划线连接：XXX_YYY_ZZZ</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>变量</h2><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;VariableTest&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args)&nbsp;{\n	//变量的定义\n	int&nbsp;myAge&nbsp;=&nbsp;12;\n	//变量的使用\n	System.out.println(myAge);\n	\n	//变量的声明\n	int&nbsp;myNumber;\n	//变量的赋值\n	myNumber&nbsp;=&nbsp;1001;\n	\n	System.out.println(myNumber);\n	}\n}</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">使用变量注意：</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">Java中每个变量必须先声明，后使用</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">使用变量名来访问这块区域的数据</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">变量的作用域：其定义所在的一对{ }内</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">变量只有在其作用域内才有效</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">同一个作用域内，不能定义重名的变量</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/6e09f0eabc1b4da39eb873d27bc2da96.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">byte:1字节=8bit位 范围 -128~127</code></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">https://blog.csdn.net/qq_23418393/article/details/57421688</p></blockquote><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;VariableTest1&nbsp;{\n	//byte范围：-128~127\n&nbsp;&nbsp;&nbsp;&nbsp;byte&nbsp;b1&nbsp;=&nbsp;12;\n&nbsp;&nbsp;&nbsp;&nbsp;byte&nbsp;b2&nbsp;=&nbsp;-128;\n&nbsp;&nbsp;&nbsp;&nbsp;//b2&nbsp;=&nbsp;128;//编译不通过\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(b1);\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(b2);\n&nbsp;&nbsp;&nbsp;&nbsp;//声明long型变量，必须以&quot;1&quot;或&quot;L&quot;结尾\n&nbsp;&nbsp;&nbsp;&nbsp;short&nbsp;s1&nbsp;=&nbsp;128;\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i1&nbsp;=&nbsp;1234;\n&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;l1&nbsp;=&nbsp;3414234324L;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(l1);\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;//浮点型\n&nbsp;&nbsp;&nbsp;&nbsp;//表示带小数点的数值\n&nbsp;&nbsp;&nbsp;&nbsp;//float表示数值的范围比long还大\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;double&nbsp;d1&nbsp;=&nbsp;123.3;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(d1&nbsp;+&nbsp;1);\n&nbsp;&nbsp;&nbsp;&nbsp;//定义float类型变量时，变量要以&quot;f&quot;或&quot;F&quot;结尾\n&nbsp;&nbsp;&nbsp;&nbsp;float&nbsp;f1&nbsp;=&nbsp;12.3F;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(f1);\n&nbsp;&nbsp;&nbsp;&nbsp;//通常，定义浮点型变量时，选用double\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;//字符型\n&nbsp;&nbsp;&nbsp;&nbsp;//定义char型变量，通常使用一对&#39;&#39;，内部只能写一个字符\n&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c1&nbsp;=&nbsp;&#39;a&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(c1);\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;//表示方法：1、声明一个字符&nbsp;2、转义字符\n&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c5&nbsp;=&nbsp;&#39;\\n&#39;;//换行符\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;c5&nbsp;=&nbsp;&#39;\\t&#39;&nbsp;//制表符\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;Hello&quot;+c5);\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;world&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;//布尔型：boolean\n&nbsp;&nbsp;&nbsp;&nbsp;//只能取两个值之一：true、false\n&nbsp;&nbsp;&nbsp;&nbsp;//常常在条件判断、循环结构中使用\n&nbsp;&nbsp;&nbsp;&nbsp;boolean&nbsp;bb1&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(bb1);\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;boolean&nbsp;isMarried&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;if(isMarried){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;祝你有一个温暖的家庭&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;}else{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;找个女朋友!&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>基本数据类型之间的运算规则：</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">前提：这里讨论的只是7种基本数据类型变量间的运算。不包含boolean类型的</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1.自动类型提升：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">结论：当容量小的数据类型的变量与容量大的数据类型的变量做运算时，结果自动提升为容量大的数据类型</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">byte 、 char 、 short --&gt; int --&gt; long --&gt; float --&gt; double</code></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">特别的：当byte、char、short三种类型的变量做运算时，结果为int型</p></blockquote><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;VariableTest2&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte&nbsp;b1&nbsp;=&nbsp;2;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i1&nbsp;=&nbsp;129;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//编译不通过\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//byte&nbsp;b2&nbsp;=&nbsp;b1&nbsp;+&nbsp;i1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i2&nbsp;=&nbsp;b1&nbsp;+&nbsp;i1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;l1&nbsp;=&nbsp;b1&nbsp;+&nbsp;i1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(i2);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;float&nbsp;f&nbsp;=&nbsp;b1&nbsp;+&nbsp;i1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(f);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c1&nbsp;=&nbsp;&#39;a&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i3&nbsp;=&nbsp;10;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i4&nbsp;=&nbsp;c1&nbsp;+&nbsp;i3;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(i4);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;short&nbsp;s2&nbsp;=&nbsp;10;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//char&nbsp;c2&nbsp;=&nbsp;c1&nbsp;+&nbsp;s2;//编译不通过\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">2.强制类型转换：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">自动类型提升运算的逆运算</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1.需要使用强转符：()</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">2.注意点：强制类型转换，可能导致精度损失。</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;VariableTest3&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;double&nbsp;d1&nbsp;=&nbsp;12.3;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i1&nbsp;=&nbsp;(int)d1;//截断操作\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(i1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//没有精度损失\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;l1&nbsp;=&nbsp;123;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;short&nbsp;s2&nbsp;=&nbsp;(short)l1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//精度损失举例2\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;i2&nbsp;=&nbsp;128;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte&nbsp;b&nbsp;=&nbsp;(byte)i2;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(b);&nbsp;//-128\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>一些注意点：</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;VariableTest4&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//编码情况：\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;l&nbsp;=&nbsp;123213;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(l);//编译通过,因为此时的l默认为int型\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;	//编译失败：过大的整数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//long&nbsp;l1&nbsp;=&nbsp;21332423235234123\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;l1&nbsp;=&nbsp;21332423235234123L;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//编译失败\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//float&nbsp;f1&nbsp;=&nbsp;12.3;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//2.编码情况2：\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//整型常量，默认类型为int型\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//浮点型常量，默认类型为double型\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte&nbsp;b&nbsp;=&nbsp;12;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//byte&nbsp;b1&nbsp;=&nbsp;b&nbsp;+&nbsp;1;//编译失败\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//float&nbsp;f1&nbsp;=&nbsp;b&nbsp;+&nbsp;12.3;//编译失败&nbsp;高精度不能直接转换为低精度\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>String类型变量的使用</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1.<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">String</code>属于引用数据类型,翻译为：字符串</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">2.声明String类型变量时，使用一对&quot;&quot;</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">3.<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">String</code>可以和8种基本数据类型变量做运算，且运算只能是连接运算 :+</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">4.运算的结果仍然是String类型</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;StringTest&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;s1&nbsp;=&nbsp;&quot;Hello&nbsp;World!&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(s1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;s2&nbsp;=&nbsp;&quot;a&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;s3&nbsp;=&nbsp;&quot;&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//char&nbsp;c&nbsp;=&nbsp;&#39;&#39;;//编译不通过，至少放个空格\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;number&nbsp;=&nbsp;1001;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;numberStr&nbsp;=&nbsp;&quot;学号：&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;info&nbsp;=&nbsp;numberStr&nbsp;+&nbsp;number;//&nbsp;+：连接运算\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;boolean&nbsp;b1&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;info1&nbsp;=&nbsp;info&nbsp;+&nbsp;b1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(info1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//练习1\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c&nbsp;=&nbsp;&#39;a&#39;;&nbsp;//97\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;num&nbsp;=&nbsp;10;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;str&nbsp;=&nbsp;&quot;hello&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(c&nbsp;+&nbsp;num&nbsp;+&nbsp;str);//107hello\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(c&nbsp;+&nbsp;str&nbsp;+&nbsp;num);//ahello10\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(c&nbsp;+&nbsp;(num&nbsp;+&nbsp;str));//a10hello\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println((c&nbsp;+&nbsp;num)&nbsp;+&nbsp;str);//107hello\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(str&nbsp;+&nbsp;num&nbsp;+&nbsp;c);//hello10a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//练习2\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//*&nbsp;*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;*&nbsp;&nbsp;&nbsp;*&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&#39;*&#39;&nbsp;+&nbsp;&quot;\\t&quot;&nbsp;+&nbsp;&#39;*&#39;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&#39;*&#39;&nbsp;+&nbsp;(&#39;\\t&#39;&nbsp;+&nbsp;&quot;*&quot;));\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>运算符</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">int&nbsp;a1&nbsp;=&nbsp;10;int&nbsp;b1&nbsp;=&nbsp;++a1;System.out.println(&quot;a1&nbsp;=&nbsp;&quot;&nbsp;+&nbsp;a1&nbsp;+&nbsp;&quot;,b1&nbsp;=&nbsp;&quot;&nbsp;+&nbsp;b1);int&nbsp;a2&nbsp;=&nbsp;10;int&nbsp;b2&nbsp;=&nbsp;a2++;System.out.println(&quot;a2&nbsp;=&nbsp;&quot;&nbsp;+&nbsp;a2&nbsp;+&nbsp;&quot;,b2&nbsp;=&nbsp;&quot;&nbsp;+&nbsp;b2);//(前)++：先自增1，然后再运算//(后)++：先运算，后自增1</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">赋值运算符</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">+=、-=、*=、/=、%=</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">逻辑运算符</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/309d8d2927a146e09ca6b1427e90843f.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">“&amp;”和“&amp;&amp;”的区别：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">单&amp;时，左边无论真假，右边都进行运算；</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">双&amp;时，如果左边为真，右边参与运算，如果左边为假，那么右边不参与运算。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">“|”和“||”的区别同理，||表示：当左边为真，右边不参与运算。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">异或( ^ )与或( | )的不同之处是：当左右都为true时，结果为false。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">理解：异或，追求的是“异”!</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">位运算符</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/c49b66118cee487ba23cdaad8f532529.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">三元运算符<br/><img src=\"https://img-blog.csdnimg.cn/d90cd489b7bd4987855a24c3e914d88f.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">凡是可以使用三元运算符的地方，都可以改写为if-else</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">反之，不成立。</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>Scanner</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如何从键盘获取不同类型的变量：需要使用Scanner类</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">具体实现步骤：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1.导包：import java.util.Scanner;</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">2.**<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Scanner</code>**的实例化</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">3.调用Scanner类的相关方法，来获取指定类型的变量</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">import&nbsp;java.util.Scanner;class&nbsp;ScannerTest&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args)&nbsp;{\n		\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Scanner&nbsp;scan&nbsp;=&nbsp;new&nbsp;Scanner(System.in);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;num&nbsp;=&nbsp;scan.nextInt();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(num);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n	}}</pre><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>算法</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">判断素数算法(优化后)：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">class&nbsp;PrimeNumberTest&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;boolean&nbsp;isFlag&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取当前时间距离的毫秒数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;start&nbsp;=&nbsp;System.currentTimeMillis();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//优化二：对本身是质数的自然数是有效的。\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(int&nbsp;j&nbsp;=&nbsp;2;&nbsp;j&nbsp;&lt;=&nbsp;Math.sqrt(i);&nbsp;j++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i&nbsp;%&nbsp;j&nbsp;==&nbsp;0){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;isFlag&nbsp;=&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;//优化一：只对本身非素数的自然数是有效的。\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isFlag&nbsp;==&nbsp;true){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(i);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//重置isFlag\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;isFlag&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;//获取当前时间距离的毫秒数\n&nbsp;&nbsp;&nbsp;&nbsp;long&nbsp;end&nbsp;=&nbsp;System.currentTimeMillis();\n&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;所花费的时间:&nbsp;&quot;&nbsp;+&nbsp;（end&nbsp;-&nbsp;start));}</pre><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>break和continue</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">break：结束当前循环，默认跳出包裹此关键字最近的一层循环</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">continue：结束当次循环</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>家庭记账程序软件</h2><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">public&nbsp;class&nbsp;FamilyAccount&nbsp;{\n	public&nbsp;static&nbsp;void&nbsp;main(String[]&nbsp;args)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;details&nbsp;=&nbsp;&quot;收支\\t账户金额\\t收支金额\\t说&nbsp;&nbsp;&nbsp;&nbsp;明\\n&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;balance&nbsp;=&nbsp;10000;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;boolean&nbsp;loopFlag&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;do&nbsp;{\n		&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;\\n-----------------家庭收支记账软件-----------------\\n&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1&nbsp;收支明细&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2&nbsp;登记收入&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3&nbsp;登记支出&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4&nbsp;退&nbsp;&nbsp;&nbsp;&nbsp;出\\n&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;请选择(1-4)：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;key&nbsp;=&nbsp;Utility.readMenuSelection();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;switch&nbsp;(key)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;&#39;1&#39;:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;-----------------当前收支明细记录-----------------&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(details);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;--------------------------------------------------&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;&#39;2&#39;:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;本次收入金额：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;amount1&nbsp;=&nbsp;Utility.readNumber();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;本次收入说明：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;desc1&nbsp;=&nbsp;Utility.readString();\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;balance&nbsp;+=&nbsp;amount1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;details&nbsp;+=&nbsp;&quot;收入\\t&quot;&nbsp;+&nbsp;balance&nbsp;+&nbsp;&quot;\\t\\t&quot;&nbsp;+\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;amount1&nbsp;+&nbsp;&quot;\\t\\t&quot;&nbsp;+&nbsp;desc1&nbsp;+&nbsp;&quot;\\n&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;---------------------登记完成---------------------&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;&#39;3&#39;:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;本次支出金额：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;amount2&nbsp;=&nbsp;Utility.readNumber();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;本次支出说明：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;desc2&nbsp;=&nbsp;Utility.readString();\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;balance&nbsp;-=&nbsp;amount2;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;details&nbsp;+=&nbsp;&quot;支出\\t&quot;&nbsp;+&nbsp;balance&nbsp;+&nbsp;&quot;\\t\\t&quot;&nbsp;+\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;amount2&nbsp;+&nbsp;&quot;\\t\\t&quot;&nbsp;+&nbsp;desc2&nbsp;+&nbsp;&quot;\\n&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&quot;---------------------登记完成---------------------&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;&#39;4&#39;:\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;确认是否退出(Y/N)：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;yn&nbsp;=&nbsp;Utility.readConfirmSelection();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(yn&nbsp;==&nbsp;&#39;Y&#39;)&nbsp;loopFlag&nbsp;=&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;while&nbsp;(loopFlag);\n	}&nbsp;&nbsp;&nbsp;&nbsp;}</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">用到的工具类Utility.class：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">import&nbsp;java.util.Scanner;/**\nUtility工具类：\n将不同的功能封装为方法，就是可以直接通过调用方法使用它的功能，而无需考虑具体的功能实现细节。\n*/public&nbsp;class&nbsp;Utility&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;static&nbsp;Scanner&nbsp;scanner&nbsp;=&nbsp;new&nbsp;Scanner(System.in);\n&nbsp;&nbsp;&nbsp;&nbsp;/**\n	用于界面菜单的选择。该方法读取键盘，如果用户键入’1’-’4’中的任意字符，则方法返回。返回值为用户键入字符。\n	*/\n	public&nbsp;static&nbsp;char&nbsp;readMenuSelection()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(;&nbsp;;&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;str&nbsp;=&nbsp;readKeyBoard(1);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c&nbsp;=&nbsp;str.charAt(0);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(c&nbsp;!=&nbsp;&#39;1&#39;&nbsp;&amp;&amp;&nbsp;c&nbsp;!=&nbsp;&#39;2&#39;&nbsp;&amp;&amp;&nbsp;c&nbsp;!=&nbsp;&#39;3&#39;&nbsp;&amp;&amp;&nbsp;c&nbsp;!=&nbsp;&#39;4&#39;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;选择错误，请重新输入：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n	/**\n	用于收入和支出金额的输入。该方法从键盘读取一个不超过4位长度的整数，并将其作为方法的返回值。\n	*/\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;int&nbsp;readNumber()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;n;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(;&nbsp;;&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;str&nbsp;=&nbsp;readKeyBoard(4);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;n&nbsp;=&nbsp;Integer.parseInt(str);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;catch&nbsp;(NumberFormatException&nbsp;e)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;数字输入错误，请重新输入：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;n;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n	/**\n	用于收入和支出说明的输入。该方法从键盘读取一个不超过8位长度的字符串，并将其作为方法的返回值。\n	*/\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;String&nbsp;readString()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;str&nbsp;=&nbsp;readKeyBoard(8);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;str;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n	\n	/**\n	用于确认选择的输入。该方法从键盘读取‘Y’或’N’，并将其作为方法的返回值。\n	*/\n&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;char&nbsp;readConfirmSelection()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;char&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(;&nbsp;;&nbsp;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;str&nbsp;=&nbsp;readKeyBoard(1).toUpperCase();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c&nbsp;=&nbsp;str.charAt(0);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(c&nbsp;==&nbsp;&#39;Y&#39;&nbsp;||&nbsp;c&nbsp;==&nbsp;&#39;N&#39;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;选择错误，请重新输入：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n	\n	\n&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;static&nbsp;String&nbsp;readKeyBoard(int&nbsp;limit)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;line&nbsp;=&nbsp;&quot;&quot;;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(scanner.hasNext())&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;line&nbsp;=&nbsp;scanner.nextLine();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(line.length()&nbsp;&lt;&nbsp;1&nbsp;||&nbsp;line.length()&nbsp;&gt;&nbsp;limit)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.print(&quot;输入长度（不大于&quot;&nbsp;+&nbsp;limit&nbsp;+&nbsp;&quot;）错误，请重新输入：&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;continue;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;line;\n&nbsp;&nbsp;&nbsp;&nbsp;}}</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"></blockquote><p><br/></p>', '河南', '2022-06-07 00:04:42', 0, 0, 0, 1);
INSERT INTO `article` VALUES (109, 60, '数据库', '数据库系统概述', '<h1 style=\"box-sizing: border-box; font-size: 24px; margin: 8px 0px 16px; line-height: 32px; color: rgb(79, 79, 79);\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统概述</h1><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">实验环境：SQL Server软件</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/b29c9118440244f9ba7375492a14366e.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>四个基本概念</h3><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义： 数据是信息的具体表现形式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">描述事物的符号记录</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义：数据库是<span style=\"box-sizing: border-box; font-weight: 700;\">长期储存</span>在计算机内、<span style=\"box-sizing: border-box; font-weight: 700;\">有组织的</span>、<span style=\"box-sizing: border-box; font-weight: 700;\">可共享</span>的<span style=\"box-sizing: border-box; font-weight: 700;\">大量</span>数据的集合</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库管理系统 DBMS</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库管理系统是位于用户与操作系统之间的一层数据管理<span style=\"box-sizing: border-box; font-weight: 700;\">软件</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">例如：SQL Server 、ORACLE、Mycle等</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>DBMS的用途</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">组织</span>和<span style=\"box-sizing: border-box; font-weight: 700;\">存储</span>好大量的数据，并提供方便、高效地<span style=\"box-sizing: border-box; font-weight: 700;\">检索</span>数据和<span style=\"box-sizing: border-box; font-weight: 700;\">维护</span>数据的手段</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>DBMS的功能</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据定义功能</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据组织、存储和管理</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据操纵功能</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库的事务管理和运行管理</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库的建立和维护功能</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">其它功能</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库系统是指在计算机系统中引入数据库后的系统</p><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统的构成</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库管理系统</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">应用系统</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库管理员(DBA)</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据管理技术的产生和发展</h3><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据管理技术的发展过程</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">人工管理阶段(40年代中–50年代中)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据不保存、数据由程序管理、数据不共享、没有独立性</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">文件系统阶段(50年代末–60年代中)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据可以长期保存，由文件系统管理、共享性和独立性差</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库系统阶段(60年代末–现在)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据长期存储、由DBMS管理、数据整体结构化、共享性和独立性较高</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统的特点</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据的管理者：DBMS</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据的结构化：整体结构化</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据的共享程度：共享性高</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据的独立性：高度的物理独立性和一定的逻辑独立性</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据控制能力：由DBMS统一管理和控制</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">数据独立性是由DBMS的<span style=\"box-sizing: border-box; font-weight: 700;\">二级映像功能</span>来保证的</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据模型</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">模型</span>是对现实世界中某个对象特征的模拟和抽象</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">数据模型</span>也是一种模型，它是对现实世界数据特征的数字化抽象</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据模型分为两类(分属两个不同的层次)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">（1)&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">概念模型</span>，也称信息模型，它是<span style=\"box-sizing: border-box; font-weight: 700;\">按用户的观点</span>来对数据和信息建模，用于数据库设计</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">(2)&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">逻辑模型和物理模型</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">逻辑模型，主要包括网状模型、层次模型、关系模型、面向对象模型等，<span style=\"box-sizing: border-box; font-weight: 700;\">按计算机系统的观点</span>对数据建模，用于DBMS实现</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">物理模型：描述数据在系统内部的表示方法和存取方法，在磁盘或磁带上的存储方式和存取方式</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>概念模型的表示方法</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">实体-联系方法</span>&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">E-R图</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">实体型</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">用矩形表示，矩形框内写明实体名</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">属性</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">用椭圆形表示，并用<span style=\"box-sizing: border-box; font-weight: 700;\">无向边</span>将其与相应的实体连接起来</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据模型的组成要素</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">数据结构</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">描述数据库的组成对象，以及对象之间的联系</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">是对系统<span style=\"box-sizing: border-box; font-weight: 700;\">静态特性</span>的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">数据操作</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">对数据库中各种对象(型)的实例(值)允许执行的操作及有关的操作规则</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据操作是对系统<span style=\"box-sizing: border-box; font-weight: 700;\">动态特性</span>的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">数据的约束条件</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">一组完整性规则的集合</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">完整性规则是对数据模型中数据及其联系定义的制约和储存规则</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系数据模型</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据之间的关联关系由Table中属性的值来表征，结构描述简单Table/relation</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据检索操作不依赖与路径信息或过程信息，支持费过程化的数据操作</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">有效支持记录集合的操作</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">较为完整的理论基础</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系模型的基本概念</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系(Relation)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">一个关系对应通常说的一张表</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">元组(Tuple)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">表中的一行即为一个元组</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">属性(Attribute)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">表中的一列即为一个属性，给每一个属性起一个名称即属性名</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统结构</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">从数据库管理系统角度看，数据库系统通常采用<span style=\"box-sizing: border-box; font-weight: 700;\">三级模式结构</span>，是<span style=\"box-sizing: border-box; font-weight: 700;\">数据库系统内部的系统结构</span></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>模式(Schema)</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">模式(也称逻辑模式)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库中<span style=\"box-sizing: border-box; font-weight: 700;\">全体数据</span>的逻辑结构和特征的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">所有用户的公共数据视图</span>，综合了所有用户的需求</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">一个数据库只有一个模式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">模式的地位：是数据库系统模式结构的<span style=\"box-sizing: border-box; font-weight: 700;\">中间层</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">与数据的物理存储细节和硬件环境无关</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">与具体的应用程序、开发工具及高级程序设计语言无关</p><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>外模式(也称子模式或用户模式)</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库用户(包括应用程序员和最终用户)使用的局部数据的逻辑结构和特征的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库用户的数据视图，是与某一应用有关的数据的逻辑表</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">外模式介于模式与应用之间</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">模式与外模式的关系：一对多</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">外模式通常是模式的子集</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">一个数据库可以有多个外模式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">对模式中同一数据，在外模式中的结构、类型、长度、保密级别等都可以不同</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">外模式与应用的关系：一对多</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">同一外模式也可为某一用户的多个应用系统所使用</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">但一个应用程序只能使用一个外模式</p></blockquote><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>内模式(也称存储模式)</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">是数据物理结构和存储方式的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">是数据在数据库内部的表示方式</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">记录的<span style=\"box-sizing: border-box; font-weight: 700;\">存储方式</span>、索引的组织方式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">数据是否压缩存储、是否加密、记录结构规定</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">一个数据库只有一个内模式</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库的二级映像功能与数据独立性</h4><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>外模式/模式 映像</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义外模式与模式之间的对应关系</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">每一个外模式都对应一个外模式/模式映像<br/><img src=\"https://img-blog.csdnimg.cn/29efa28f82a3495ba8a2d3f65d8834e2.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>模式/内模式映像</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">模式/内模式映像定义了数据全局逻辑结构与存储结构之间的对应关系。例如：说明逻辑记录和字段在内部是如何表示的。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库中模式/内模式映像是唯一的</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">映像定义通常包含在各自外模式的描述中</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/f7b076a6657b44a399d1f1cf413afaf7.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>数据库系统的组成</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库管理系统(及其开发工具)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">应用系统</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据库管理员</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系数据库</h2><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系模型的三要素</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系数据结构：Relation/Table</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">基本操作：Relation Operator</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">完整性约束：实体完整性、参照完整性和用户自定义完整性</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系模型与关系数据库语言的关系</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系运算：关系代数和关系演算；</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系演算：元祖演算和域演算</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系代数示例：基于集合的运算</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">基于关系代数设计的数据库语言(ISBL):用计算机可识别的符号表征关系代数的运算符号</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">元组演算示例：基于逻辑的运算</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">基于元组演算设计的数据库语言：用计算机可识别的符号表征元组演算的运算符号</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">域演算示例：基于示例的运算</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">基于域演算设计的数据库言语(QBE:Query By Example)</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>域</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义：域是一组具有相同数据类型的值的集合</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>笛卡尔积</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/b85eea28f659415aab3fe0a7b6e2d9a4.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/daaf02a4143b4d7fb3c57ff8ab9078ab.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/042e2e7d49fa4efe8ab5dd67965519ac.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/860d315419a34cd3a1773a6d4076ef03.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/4d36aa5a93e44655915354d44297bb9b.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/f6fa6fecc76d4b018503469afd3de631.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/5912cb587b024e8e959f06eb00762273.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/4c2ee97abaa7440c8f21e2861af075f5.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>基本关系模式</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义：关系的描述称为关系模式，关系模式可以形式化地表示为：</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">R(U,D,dom,F)</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">R 关系名</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">U 组成该关系的属性名集合</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">D 属性组U中属性所来自的域</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">dom 属性向域的映像集合</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">F 属性间的数据依赖关系集合</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">[外链图片转存失败,源站可能有防盗链机制,建议将图片保存下来直接上传(img-rTcUU2Nn-1631534040942)(E:\\CSDN\\DBS\\IMG\\关系模式.jpg)]</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系模式和关系</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系模式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">对关系的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">静态的、稳定的</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系模式在某一时刻的状态或内容</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">动态的、随时间不断变化的</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">关系模式和关系往往统称为关系，通过上下文加以区别</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系数据库</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在一个给定的应用领域中，所有实体及实体之间联系的关系的集合构成一个关系数据库</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系数据库也有<span style=\"box-sizing: border-box; font-weight: 700;\">型</span>和<span style=\"box-sizing: border-box; font-weight: 700;\">值</span>之分</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系数据库的型称为<span style=\"box-sizing: border-box; font-weight: 700;\">关系数据库模式</span>，是对关系数据库的描述</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">若干域的定义</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在这些域上定义的若干关系模式</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系数据库的值是这些关系模式在某一时刻对应的关系的集合，通常简称为关系数据库</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>基本的关系操作</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">查询</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">基本操作：选择、投影、并、差、笛卡尔积</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">扩展操作：连接、除、交</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">数据更新</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">插入、删除、修改</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">其中选择、投影、并、差、笛卡尔积是五种基本操作 其他操作可以用基本操作来定义和导出</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系数据语言的分类</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/d3a55b7e974540f18379cb0c3ce85a5c.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>关系的完整性</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系模型中三类完整性约束</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">实体完整性和参照完整性是关系模型必须满足的完整性约束条件，被称作是关系的两个不变性。应该由关系系统自动支持。</p></blockquote><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>实体完整性</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在关系模型中实体及实体间的联系都是用关系来描述的，因此可能存在着关系与关系间的引用</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/8a0fa951c51c4478911266d5f8de6234.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>参照完整性</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在关系模型中实体及实体间的联系都是用关系来描述的，因此可能存在着关系与关系间的引用</p><h5 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 24px; color: rgb(79, 79, 79); font-size: 16px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>外码</h5><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/6f01a2a75883418d959cf1422773f244.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/65a0e56fbb78463b89af23a0e3d1a5a2.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/4ae666c42804445bb654c71f24452460.jpg?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBA5LmQ5LmQ5LmQ5LmQ5Z-5,size_20,color_FFFFFF,t_70,g_se,x_16#pic_center\" alt=\"在这里插入图片描述\"/></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>用户定义的完整性</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">用户定义的完整性是针对某一具体关系数据库的约束条件，反映某一具体应用所涉及的数据必须满足的语义要求。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">关系模型应提供定义和检验这类完整性的机制，以便用统一的系统的方法处理它们，而不要由应用程序承担这一功能</p><p><br/></p>', '河南', '2022-06-07 00:06:08', 0, 0, 1, 1);
INSERT INTO `article` VALUES (110, 60, '人工智能', '硬布线控制器和微程序控制器', '<p><img src=\"https://img-blog.csdnimg.cn/20210515101618714.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><br/></p><h1 style=\"box-sizing: border-box; font-size: 24px; margin: 8px 0px 16px; line-height: 32px; color: rgb(79, 79, 79);\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>控制器</h1><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>控制器的结构和功能</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">控制器是计算机系统的指挥中心，控制器的<span style=\"box-sizing: border-box; font-weight: 700;\">主要功能</span>有：<br/>1）从主存中取出一条指令，并指出下一条指令在主存中的位置；<br/>2）对指令进行译码或测试，产生相应的操作控制信号，以便启动规定的动作；<br/>3）指挥并控制CPU、主存、输入和输出设备之间的<span style=\"box-sizing: border-box; font-weight: 700;\">数据流动方向</span></p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>硬布线控制器</h2><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">又称硬连线控制器</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>硬布线控制器的定义</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">硬布线控制器是一种由<span style=\"box-sizing: border-box; font-weight: 700;\">门电路</span>和<span style=\"box-sizing: border-box; font-weight: 700;\">触发器</span>构成的复杂树形网络.</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">是早期设计计算机的一种方法；<br/>以使用最少原件和取得最高操作速度为设计目标；<br/>是计算机中最复杂的逻辑部件；<br/>一旦构成，无法增加新的控制功能。</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">与微程序控制相比，<span style=\"box-sizing: border-box; font-weight: 700;\">硬布线控制的速度较快</span></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>硬布线控制器结构</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/20210515094641278.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/20210515111351471.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>逻辑网络输入信号的三个来源</h4><ol style=\"box-sizing: border-box; margin-bottom: 24px; padding: 0px; overflow: auto hidden;\" class=\" list-paddingleft-2\"><li><p>来自<span style=\"box-sizing: border-box; font-weight: 700;\">指令操作码译码器</span>的输出I；</p></li><li><p>来自<span style=\"box-sizing: border-box; font-weight: 700;\">执行部件的反馈信息</span>B；</p></li><li><p>来自<span style=\"box-sizing: border-box; font-weight: 700;\">时序产生器的时序信号</span>M和T；</p></li></ol><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">时序信号包括节拍电位信号M和节拍脉冲信号T；其中节拍电位信号就是机器周期(CPU周期)信号，节拍脉冲信号是时钟周期信号。</p></blockquote><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>硬布线控制器的基本原理</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">某一<span style=\"box-sizing: border-box; font-weight: 700;\">微操作控制信号C</span>是指令操作码译码器输出I、时序信号(节拍电位M、节拍脉冲T)、和状态条件信号B的<span style=\"box-sizing: border-box; font-weight: 700;\">逻辑函数</span>，即</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">C = f(I,M,T,B)</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">逻辑网络N的输出信号就是微操作控制信号，它用来对执行部件进行控制。</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微操作控制信号</h3><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微操作控制信号的产生</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在硬布线控制器中，某一微操作控制信号由<span style=\"box-sizing: border-box; font-weight: 700;\">布尔代数表达式描述的输出函数</span>产生；</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>安排微操作时序的原则</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">原则一 ：微操作的<span style=\"box-sizing: border-box; font-weight: 700;\">先后顺序不得</span>随意<span style=\"box-sizing: border-box; font-weight: 700;\">更改</span><br/>原则二：<span style=\"box-sizing: border-box; font-weight: 700;\">被控对象不同</span>的微操作尽量安排在<span style=\"box-sizing: border-box; font-weight: 700;\">一个节拍</span>内完成(即两个微操作没有冲突时)<br/>原则三：占用<span style=\"box-sizing: border-box; font-weight: 700;\">时间较短</span>的微操作尽量安排在<span style=\"box-sizing: border-box; font-weight: 700;\">一个节拍</span>内完成，并<span style=\"box-sizing: border-box; font-weight: 700;\">允许有先后顺序</span></p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微程序控制器</h2><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微命令和微操作</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">微命令</span>：是微操作的控制信号<br/><span style=\"box-sizing: border-box; font-weight: 700;\">微操作</span>：是微命令的执行过程</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">相容性微命令</span>：可以同时产生、共同完成某一些微操作的微命令<br/><span style=\"box-sizing: border-box; font-weight: 700;\">互斥性微命令</span>&nbsp;：在机器中不允许同时出现的微命令</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">相容性微操作</span>&nbsp;：是指在<span style=\"box-sizing: border-box; font-weight: 700;\">同时</span>或同一个CPU周期内可以<span style=\"box-sizing: border-box; font-weight: 700;\">并行</span>执行的微操作<br/><span style=\"box-sizing: border-box; font-weight: 700;\">相斥性微操作</span>：是指<span style=\"box-sizing: border-box; font-weight: 700;\">不能在同时</span>或<span style=\"box-sizing: border-box; font-weight: 700;\">不能</span>在同一个CPU周期内<span style=\"box-sizing: border-box; font-weight: 700;\">并行</span>执行的微操作</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">如：加法指令ADD X中<br/>PC→MAR和1→R互不影响，是相容的<br/>MDR→IR和OR(IR)→ID有先后顺序之分，是互斥的</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/20210515175439702.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微指令和微程序</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">微指令</span>： 一个CPU周期中，实现一定操作功能的一组微命令的组合<br/><span style=\"box-sizing: border-box; font-weight: 700;\">微程序</span>：能实现一条机器指令功能的多条微指令序列</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微指令基本格式</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如下两图：<br/><img src=\"https://img-blog.csdnimg.cn/20210515175821670.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/><br/><img src=\"https://img-blog.csdnimg.cn/20210515175848229.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>程序与微程序</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">程序是指令的有序集合，用于完成特定的功能<br/>微程序是微指令的有序集合，一条指令的功能由一段微程序来实现</p><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微程序控制器</h4><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">主要由<span style=\"box-sizing: border-box; font-weight: 700;\">控制存储器</span>、<span style=\"box-sizing: border-box; font-weight: 700;\">微指令寄存器</span>、<span style=\"box-sizing: border-box; font-weight: 700;\">地址转移逻辑</span>三大部分组成，其中为指令寄存器分为<span style=\"box-sizing: border-box; font-weight: 700;\">微地址寄存器</span>和<span style=\"box-sizing: border-box; font-weight: 700;\">微命令寄存器</span>两部分</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">微指令一般包含操作控制和顺序控制两大部分<br/>操作控制：用于发出管理和指挥全机工作的控制信号<br/>顺序控制：用与决定产生下一条微指令的地址</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">所有的微指令都存放于控制存储器中，使用<span style=\"box-sizing: border-box; font-weight: 700;\">微地址</span>访问</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">微周期：通常指从控制存储器中读取<span style=\"box-sizing: border-box; font-weight: 700;\">一条</span>微指令并执行相应的微操作所需的时间</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>主存储器和控制存储器</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">主存储器</span>：用于存放程序和数据，在CPU外部，用RAM和ROM实现<br/><span style=\"box-sizing: border-box; font-weight: 700;\">控制存储器(CM)</span>：用于存放微程序，在CPU内部，用ROM实现<br/><img src=\"https://img-blog.csdnimg.cn/20210515175033651.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"控制存储器参考图\"/></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微命令编码</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">微命令编码，就是对微指令中的操作控制字段采用的表示方法。通常有以下三种方法：</p><ol style=\"box-sizing: border-box; margin-bottom: 24px; padding: 0px; overflow: auto hidden;\" class=\" list-paddingleft-2\"><li><p>直接表示法：<br/>操作控制字段的每一位代表一个微命令<br/><img src=\"https://img-blog.csdnimg.cn/20210515180233203.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/><br/>优点：简单直观，其输出可直接用于控制，执行速度快<br/>缺点：微指令字较长，因而使控制存储器容量较大</p></li><li><p>编码表示法<br/>编码表示法是把一组相斥性的微命令信号组成一个小组(即一个字段)，然后通过小组(字段)译码器对每一个微命令信号进行译码，译码输出作为操作控制信号<br/><img src=\"https://img-blog.csdnimg.cn/20210515180726772.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/><br/>优点：可大大缩短微指令字长<br/>缺点：<span style=\"box-sizing: border-box; font-weight: 700;\">需要微命令译码</span>，故微程序的执行速度稍稍减慢</p></li><li><p>混合表示法<br/>把直接表示法与字段编码法混合使用，以便能综合考虑微指令字长，灵活性、执行微程序速度等方面的要求</p></li></ol><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微地址形成方式</h3><ol style=\"box-sizing: border-box; margin-bottom: 24px; padding: 0px; overflow: auto hidden;\" class=\" list-paddingleft-2\"><li><p>微指令的<span style=\"box-sizing: border-box; font-weight: 700;\">下地址字段</span>指出</p></li></ol><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">微指令格式中设置一个下地址字段，由微指令的下地址字段直接指出后继微指令的地址，这种方式又称<span style=\"box-sizing: border-box; font-weight: 700;\">断定方式</span></p></blockquote><ol style=\"box-sizing: border-box; margin-bottom: 24px; padding: 0px; overflow: auto hidden;\" class=\" list-paddingleft-2\"><li><p>根据机器指令的<span style=\"box-sizing: border-box; font-weight: 700;\">操作码</span>形成</p></li></ol><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">当机器指令取至指令寄存器后，微指令的地址由操作码经微地址形成部件形成</p></blockquote><ol style=\"box-sizing: border-box; margin-bottom: 24px; padding: 0px; overflow: auto hidden;\" class=\" list-paddingleft-2\"><li><p>增量计数器法 (CMAR)+1→CMAR</p></li><li><p>分支转移<br/>转移方式：指明判别条件<br/>转移地址：指明转移成功后的去向<br/>| 操作控制字段 | 转移方式 | 转移地址 |</p></li><li><p>通过测试网络</p></li><li><p>由硬件产生微程序入口地址<br/><span style=\"box-sizing: border-box; font-weight: 700;\">第一条微指令地址</span>&nbsp;由专门<span style=\"box-sizing: border-box; font-weight: 700;\">硬件</span>产生<br/>中断周期：由<span style=\"box-sizing: border-box; font-weight: 700;\">硬件</span>产生<span style=\"box-sizing: border-box; font-weight: 700;\">中断周期微程序首地址</span></p></li></ol><p><br/></p>', '河南', '2022-06-07 00:09:10', 0, 0, 0, 1);
INSERT INTO `article` VALUES (111, 60, 'Cpp', 'C++方法重载', '<h2 style=\"box-sizing: border-box; margin: 0px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\">函数重载概念</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在同一个类中，可以存在一个以上的<span style=\"box-sizing: border-box; font-weight: 700;\">同名方法</span>，只要它们的数据类型不同或参数出现的顺序不同或个数不同即可</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>重载特点</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1、允许两个以上的同名函数<br/>2、函数之间的参数组合必须不同（参数的数目不同或者参数的类型不同或者参数出现的顺序不同）<br/>3、函数的返回值不能作为判断函数之间是否构成重载的依据</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>重载好处</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1、优化了主函数main()里的代码，使得主函数更有可读性，优化设计<br/>2、培养了“黑盒思想“，将每一个函数当成一个工具使用可以减少我们需要记忆的东西，是我们的程序更加美观</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>代码演示</h2><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">#include&nbsp;&lt;bits/stdc++.h&gt;using&nbsp;namespace&nbsp;std;//函数的重载：函数名相同，参数表不同（数据类型不同或参数出现的顺序不同或个数不同）bool&nbsp;isTriangle(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c);bool&nbsp;isTriangle(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c);bool&nbsp;isTriangle(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c);bool&nbsp;isValidSide(int&nbsp;a);bool&nbsp;isValidSide(float&nbsp;a);bool&nbsp;isValidSide(double&nbsp;a);bool&nbsp;isTrue(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c);bool&nbsp;isTrue(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c);bool&nbsp;isTrue(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c);int&nbsp;SeekCircumference(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c);float&nbsp;SeekCircumference(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c);double&nbsp;SeekCircumference(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c);int&nbsp;main(){\n&nbsp;&nbsp;&nbsp;&nbsp;float&nbsp;fa,&nbsp;fb,&nbsp;fc;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;Input&nbsp;three&nbsp;sides&nbsp;of&nbsp;a&nbsp;Triangle:&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;fa&nbsp;&gt;&gt;&nbsp;fb&nbsp;&gt;&gt;&nbsp;fc;\n&nbsp;&nbsp;&nbsp;&nbsp;if(isTriangle(fa,fb,fc))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;fa&nbsp;&lt;&lt;&nbsp;&quot;,&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;fb&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;and&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;fc&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;can&nbsp;constitute&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;is&nbsp;an&nbsp;invalid&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;三角形的周长是:&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;SeekCircumference(fa,fb,fc)&nbsp;&lt;&lt;&nbsp;endl;\n\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;a,&nbsp;b,&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;Input&nbsp;three&nbsp;sides&nbsp;of&nbsp;a&nbsp;Triangle:&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;a&nbsp;&gt;&gt;&nbsp;b&nbsp;&gt;&gt;&nbsp;c;\n&nbsp;&nbsp;&nbsp;&nbsp;if(isTriangle(a,b,c))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;a&nbsp;&lt;&lt;&nbsp;&quot;,&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;b&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;and&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;c&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;can&nbsp;constitute&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;is&nbsp;an&nbsp;invalid&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;三角形的周长是:&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;SeekCircumference(a,b,c)&nbsp;&lt;&lt;&nbsp;endl;\n\n&nbsp;&nbsp;&nbsp;&nbsp;double&nbsp;da,&nbsp;db,&nbsp;dc;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;Input&nbsp;three&nbsp;sides&nbsp;of&nbsp;a&nbsp;Triangle:&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;cin&nbsp;&gt;&gt;&nbsp;da&nbsp;&gt;&gt;&nbsp;db&nbsp;&gt;&gt;&nbsp;dc;\n&nbsp;&nbsp;&nbsp;&nbsp;if(isTriangle(da,db,dc))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;da&nbsp;&lt;&lt;&nbsp;&quot;,&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;db&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;and&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;dc&nbsp;&lt;&lt;&nbsp;&quot;&nbsp;can&nbsp;constitute&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;is&nbsp;an&nbsp;invalid&nbsp;Triangle!&quot;&nbsp;&lt;&lt;&nbsp;endl;\n&nbsp;&nbsp;&nbsp;&nbsp;cout&nbsp;&lt;&lt;&nbsp;&quot;三角形的周长是:&nbsp;&quot;&nbsp;&lt;&lt;&nbsp;SeekCircumference(da,db,dc)&nbsp;&lt;&lt;&nbsp;endl;\n\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;0;}bool&nbsp;isTriangle(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;边界条件\n&nbsp;&nbsp;&nbsp;&nbsp;if(isValidSide(a)&amp;&amp;isValidSide(b)&amp;&amp;isValidSide(c))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isTrue(a,b,c)&amp;&amp;&nbsp;isTrue(b,c,a)&nbsp;&amp;&amp;&nbsp;isTrue(c,a,b))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isValidSide(int&nbsp;a){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a&gt;0)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isTrue(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a+b&gt;c)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isTriangle(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;边界条件\n&nbsp;&nbsp;&nbsp;&nbsp;if(isValidSide(a)&amp;&amp;isValidSide(b)&amp;&amp;isValidSide(c))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isTrue(a,b,c)&amp;&amp;&nbsp;isTrue(b,c,a)&nbsp;&amp;&amp;&nbsp;isTrue(c,a,b))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isValidSide(float&nbsp;a){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a&gt;0)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isTrue(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a+b&gt;c)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isTriangle(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;边界条件\n&nbsp;&nbsp;&nbsp;&nbsp;if(isValidSide(a)&amp;&amp;isValidSide(b)&amp;&amp;isValidSide(c))\n&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(isTrue(a,b,c)&amp;&amp;&nbsp;isTrue(b,c,a)&nbsp;&amp;&amp;&nbsp;isTrue(c,a,b))\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isValidSide(double&nbsp;a){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a&gt;0)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}bool&nbsp;isTrue(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;if(a+b&gt;c)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;else\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;}int&nbsp;SeekCircumference(int&nbsp;a,&nbsp;int&nbsp;b,&nbsp;int&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;int&nbsp;C=a+b+c;\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;C;}float&nbsp;SeekCircumference(float&nbsp;a,&nbsp;float&nbsp;b,&nbsp;float&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;float&nbsp;C=a+b+c;\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;C;}double&nbsp;SeekCircumference(double&nbsp;a,&nbsp;double&nbsp;b,&nbsp;double&nbsp;c){\n&nbsp;&nbsp;&nbsp;&nbsp;double&nbsp;C=a+b+c;\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;C;}</pre><p><br/></p>', '河南', '2022-06-07 00:12:10', 1, 0, 1, 1);
INSERT INTO `article` VALUES (112, 60, '系统/运维', 'Git（上）', '<p>Git是分布式版本控制系统，极其强大的分支管理</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><br/></p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>集中式版本控制系统</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">版本库是集中存放在中央服务器的，需联网才能工作</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>分布式版本控制系统</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">每个人电脑上都有一个完整的版本库，不需联网</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>在Git Bash中设置用户名和邮箱</h2><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">$&nbsp;git&nbsp;config&nbsp;--global&nbsp;user.name&nbsp;&quot;LWP13226968149&quot;\n$&nbsp;git&nbsp;config&nbsp;--global&nbsp;user.email&nbsp;&quot;LWP13226968149@163.com&quot;</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">注意<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git config</code>命令的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">--global</code>参数，用了这个参数，表示你这台机器上所有的Git仓库都会使用这个配置，当然也可以对某个仓库指定不同的用户名和Email地址。</p></blockquote><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>版本库(.git)</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">又名仓库（repository），可以简单理解成一个目录。这个目录里面的所有文件都可以被Git管理起来；</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>创建一个版本库并简单添加一个文件的步骤</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">首先，选择一个合适的地方，创建一个空目录：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">$&nbsp;mkdir&nbsp;JavaWeb\n$&nbsp;cd&nbsp;Javaweb\n$&nbsp;pwd</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">$ pwd</code>&nbsp;命令用于显示当前目录</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">仓库所在目录名最好不包含中文</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第二步，通过<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git init</code>命令把这个目录变成Git可以管理的仓库：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">$&nbsp;git&nbsp;init</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第三步，新建一个<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">.txt</code>文件，将其放入<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">learngit</code>目录下，通过<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git add&nbsp;</code>命令把文件添加到仓库中：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">$&nbsp;git&nbsp;add&nbsp;readme.txt</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第四步，用命令<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git commit</code>把文件提交到仓库中</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">$&nbsp;git&nbsp;commit&nbsp;-m&nbsp;&quot;lep&nbsp;is&nbsp;a&nbsp;handsome&nbsp;boy&quot;</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">-m 后面的字符串是对本次提交的说明</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git status</code>查看仓库当前的状态</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git diff</code>查看修改</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><ul style=\"box-sizing: border-box; padding: 0px; font-size: 14px; overflow: auto hidden; line-height: 22px;\" class=\" list-paddingleft-2\"><li><p>要随时掌握工作区的状态，使用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git status</code>命令。</p></li><li><p>如果<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git status</code>告诉你有文件被修改过，用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git diff</code>可以查看修改内容。</p></li></ul></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git log</code>显示从最近到最远的提交日志</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git reset --hard &lt;commit id&gt;/&lt;HEAD^&gt;</code>&nbsp;回退版本</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD</code>表示当前版本，（注意我的提交ID和你的肯定不一样），上一个版本就是<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD^</code>，上上一个版本就是<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD^^</code>，当然往上100个版本写100个<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">^</code>比较容易数不过来，所以写成<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD~100</code></p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">cat readme.txt</code>查看<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">readme.txt</code>的内容</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">Git的版本回退速度非常快，因为Git在内部有个指向当前版本的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD</code>指针，当你回退版本的时候，Git仅仅是把HEAD从指向<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">append GPL</code>：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">┌────┐\n│HEAD│\n└────┘\n&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;└──&gt;&nbsp;○&nbsp;append&nbsp;GPL\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;○&nbsp;add&nbsp;distributed\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;○&nbsp;wrote&nbsp;a&nbsp;readme&nbsp;file</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">改为指向<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">add distributed</code>：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px;\">┌────┐\n│HEAD│\n└────┘\n&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;│&nbsp;&nbsp;&nbsp;&nbsp;○&nbsp;append&nbsp;GPL\n&nbsp;&nbsp;&nbsp;│&nbsp;&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;└──&gt;&nbsp;○&nbsp;add&nbsp;distributed\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;│\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;○&nbsp;wrote&nbsp;a&nbsp;readme&nbsp;file</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git reflog</code>命令 记录了你的每一次命令</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>工作区和暂存区</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">工作区：在电脑上能看到的目录</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">暂存区：工作区中隐藏目录.git是Git的版本库，版本库中主要的有暂存区(stage)和Git为我们创建的第一个分支(master)，以及指向<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">master</code>的一个指针<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">HEAD</code></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/4079f07b7d9d486fab1ea493aa4c4100.jpg?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git add</code>命令实际上就是把要提交的所有修改放到暂存区（Stage），然后，</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">执行<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git commit</code>就可以一次性把暂存区的所有修改提交到分支。</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">Git管理的是修改，而不是文件！</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">就好比你用git add命令添加了你刚刚对<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">readme.txt</code>文件的修改，在提交前你又更改了工作区里<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">readme.txt</code>文件的内容，此时用git commit提交 用git status命令查看状态会发现提交前第二次的修改并未提交</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">当用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git add</code>将文件添加到暂存区时，可以用</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git restore --stage readme.txt</code>命令撤销上传，回到<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git add</code>前的状态</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git restore readme.txt</code>命令来放弃工作目录中的修改</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><br/></h3><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\">命令表<br/><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a></h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/6131f1fe87434dc293213dc229fb41c3.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><p><br/></p>', '河南', '2022-06-07 00:15:38', 0, 2, 2, 1);
INSERT INTO `article` VALUES (113, 60, '系统/运维', 'Git（下）', '<h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\">删除文件</h3><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">rm &lt;file&gt;</code>可以把文件删了，当然，你也可以在目录下直接删了</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">这时，git中工作区和版本库就不一致了，<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git status</code>命令会立刻告诉你哪些文件被删除了：</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/482f2001a29c487c823c41a092808b5f.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">这时就有两种选择，第一：可以从版本库中拉取回来，把误删的文件恢复到最新版本</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git restore test.txt</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">这样，被我删掉的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">test.txt</code>就恢复了；</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">当然，你也可以让版本库和工作区一样也删掉该文件：</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git rm test.txt</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/4280e7a11e224d868794fff3a269cdc7.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><h2 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>远程仓库</h2><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>添加SSH密钥</h3><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">由于你的本地Git仓库和<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>仓库之间的传输是通过SSH加密的，所以，需要一点设置：</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第1步，创建SSH Key。在用户 主目录（例如我的是C:\\Users\\Administrator）查看是否有.ssh目录；</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如果有，则打开目录查看是否有<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa</code>和<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa.pub</code>这两个文件，如果也已经有了，则直接到第2步；</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如果没有,通过命令：</p><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">ssh-keygen -t rsa -C &quot;LWP13226968149@163.com&quot;</code></p></blockquote><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">创建SSH Key（这里的邮箱是我的，该命令把邮箱改为你的就行）</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">创建完成后，用户主目录里会有<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">.ssh</code>目录，里面有<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa</code>和<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa.pub</code>两个文件</p><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa</code>是私钥，<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa.pub</code>是公钥</p></blockquote><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第2步，登录<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>，在主界面右上角点击头像-Settings，打开后可以看到左边有一栏<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Account settings</code>,找到<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">SSH and GPG keys</code>，然后，点<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">new SSH Key</code>，Title填方便记忆你喜欢的就行，在Key文本框里面粘贴<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">id_rsa.pub</code>文件里的内容，点<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Add Key</code>，添加完毕</p><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">在<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Gitee</code>上也是一样的操作</p></blockquote><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>添加远程库</h3><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">登录<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>，找到<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Create a new repo</code>，创建一个新的仓库，命名随意你喜欢，成功在<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Github</code>创建了Git仓库</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">现在把本地仓库与之关联:</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;remote&nbsp;add&nbsp;origin&nbsp;git@github.com:LWP123456789/JavaWeb.git</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">把本地仓库的内容推送到<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Github</code>仓库：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;push&nbsp;-u&nbsp;origin&nbsp;master</pre><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git push</code>命令，实际上是把当前分支master推送到远程</p></blockquote><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">第一次推送master分支时，使用<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">-u</code>参数，Git不但会把本地的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">master</code>分支内容推送的远程新的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">master</code>分支，还会把本地的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">master</code>分支和远程的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">master</code>分支关联起来，在以后的推送或者拉取时就可以简化命令。</p></blockquote><h4 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>SSH警告</h4><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">当你第一次使用Git的<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">clone</code>或者<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">push</code>命令连接<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>时，会得到一个警告：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">The&nbsp;authenticity&nbsp;of&nbsp;host&nbsp;&#39;github.com&nbsp;(xx.xx.xx.xx)&#39;&nbsp;can&#39;t&nbsp;be&nbsp;established.\nRSA&nbsp;key&nbsp;fingerprint&nbsp;is&nbsp;xx.xx.xx.xx.xx.\nAre&nbsp;you&nbsp;sure&nbsp;you&nbsp;want&nbsp;to&nbsp;continue&nbsp;connecting&nbsp;(yes/no)?</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">这是因为Git使用SSH连接，而SSH连接在第一次验证<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>服务器的Key时，需要你确认<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>的Key的指纹信息是否真的来自<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>的服务器，输入<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">yes</code>回车即可。</p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">Git会输出一个警告，告诉你已经把<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">GitHub</code>的Key添加到本机的一个信任列表里了：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">Warning:&nbsp;Permanently&nbsp;added&nbsp;&#39;github.com&#39;&nbsp;(RSA)&nbsp;to&nbsp;the&nbsp;list&nbsp;of&nbsp;known&nbsp;hosts.</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">这个警告只会出现一次，后面的操作就不会有任何警告了。</p><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>删除远程库</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;remote&nbsp;rm&nbsp;&lt;name&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">删除远程库前通常先查看远程库信息：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;remote&nbsp;-v</pre><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">实际上是解除了本地和远程的绑定关系，要想真正完全删除远程库，登录到<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">Github</code>在库中Settings删除即可</p></blockquote><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>克隆远程库</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;clone&nbsp;&lt;HTTPS/SSH/GitHub&nbsp;CLI&gt;</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/b298aa3c1aa84ab2894772643b7ff5c9.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70#pic_center\" alt=\"在这里插入图片描述\"/></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如图，可以<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">在Github</code>仓库中保存下载地址</p><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>推送分支</h3><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">把该分支上的所有本地提交推送到远程库，推送时，要指定本地分支，这样，Git就会把该分支推送到远程库对应的远程分支上：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;push&nbsp;origin&nbsp;master</pre><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">如果要推送其他分支，比如<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">dev</code>，就改成：</p><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;push&nbsp;origin&nbsp;dev</pre><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>抓取分支</h3><h2 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>创建、查看、切换、合并分支的指令</h2><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">查看分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git branch</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">创建分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git branch &lt;name&gt;</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">切换分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git checkout &lt;name&gt;</code>或者<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git switch &lt;name&gt;</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">创建+切换分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git checkout -b &lt;name&gt;</code>或者<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git switch -c &lt;name&gt;</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">合并某分支到当前分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git merge &lt;name&gt;</code></p><p style=\"margin-top: 0px; margin-bottom: 16px; white-space: normal; box-sizing: border-box; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">删除分支：<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git branch -d &lt;name&gt;</code></p><blockquote style=\"white-space: normal; box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git merge</code>&nbsp;与&nbsp;<code style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(249, 242, 244); border-radius: 2px; padding: 2px 4px; line-height: 22px; color: rgb(199, 37, 78);\">git rebase</code>&nbsp;的区别</p><p style=\"margin-top: 0px; margin-bottom: 0px; box-sizing: border-box; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\"><a href=\"https://editor.csdn.net/md/?articleId=119549746\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219); text-decoration-line: none;\">https://blog.csdn.net/liuxiaoheng1992/article/details/79108233</a></p></blockquote><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>标签管理</h3><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>查看标签</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;tag</pre><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>创建标签</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;tag&nbsp;&lt;name&gt;</pre><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>删除标签</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;tag&nbsp;-d&nbsp;&lt;name&gt;</pre><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>推送某个标签到远程</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;push&nbsp;origin&nbsp;&lt;tagname&gt;</pre><h3 style=\"white-space: normal; box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; color: rgb(78, 161, 219);\"></a>删除已推送到远程的标签</h3><pre style=\"margin-top: 1.2em; margin-bottom: 24px; box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; line-height: 22px;\">$&nbsp;git&nbsp;tag&nbsp;-d&nbsp;&lt;name&gt;\n$&nbsp;git&nbsp;push&nbsp;origin:ref/tags/&lt;names&gt;</pre><p><br/></p>', '河南', '2022-06-07 00:15:24', 2, 2, 4, 1);
INSERT INTO `article` VALUES (114, 60, 'HarmonyOS', 'HarmonyOS 概述', '<p>1、系统定义</p><p>HarmonyOS 是一款“面向未来”、面向全场景（移动办公、运动健康、社交通信、媒体娱乐等）的分布式操作系统。在传统的单设备系统能力的基础上，HarmonyOS 提出了基于同一套系统能力、适配多种终端形态的分布式理念，能够支持多种终端设备。</p><p>⚫ 对消费者而言，HarmonyOS 能够将生活场景中的各类终端进行能力整合，形成一个</p><p>“超级虚拟终端”，可以实现不同的终端设备之间的快速连接、能力互助、资源共</p><p>享，匹配合适的设备、提供流畅的全场景体验。</p><p>⚫ 对应用开发者而言，HarmonyOS 采用了多种分布式技术，使得应用程序的开发实现</p><p>与不同终端设备的形态差异无关，降低了开发难度和成本。这能够让开发者聚焦上层</p><p>业务逻辑，更加便捷、高效地开发应用。</p><p>⚫ 对设备开发者而言，HarmonyOS 采用了组件化的设计方案，可以根据设备的资源能</p><p>力和业务特征进行灵活裁剪，满足不同形态的终端设备对于操作系统的要求。</p><p><br/></p><p>2、技术架构</p><p>HarmonyOS 整体遵从分层设计，从下向上依次为：内核层、系统服务层、框架层和应用</p><p>层。系统功能按照“系统 &gt; 子系统 &gt; 功能/模块”逐级展开，在多设备部署场景下，支</p><p>持根据实际需求裁剪某些非必要的子系统或功能/模块。</p><p>2.1 内核层</p><p>• 内核子系统：HarmonyOS 采用多内核设计，支持针对不同资源受限设备选用适合的 OS内核。内核抽象层（KAL，KernelAbstract Layer）通过屏蔽多内核差异，对上层提供基础的内核能力，包括进程/线程管理、内存管理、文件系统、网络管理和外设管理等。</p><p>• 驱动子系统：HarmonyOS 驱动框架（HDF）是 HarmonyOS 硬件生态开放的基础，提供统一外设访问能力和驱动开发、管理框架。</p><p>2.2 系统服务层</p><p>系统服务层是 HarmonyOS 的核心能力集合，通过框架层对应用程序提供服务。该层包含以下几个部分：</p><p>• 系统基本能力子系统集：为分布式应用在 HarmonyOS 多设备上的运行、调度、迁移等操作提供了基础能力，由分布式软总线、分布式数据管理、分布式任务调度、方舟多语言运行时、公共基础库、多模输入、图形、安全、AI 等子系统组成。其中，方舟运行时提供了C/C++/JS 多语言运行时和基础的系统类库，也为使用方舟编译器静态化的 Java 程序（即应用程序或框架层中使用 Java 语言开发的部分）提供运行时。</p><p>• 基础软件服务子系统集：为 HarmonyOS 提供公共的、通用的软件服务，由事件通知、电话、多媒体、DFX、MSDP&amp;DV 等子系统组成。</p><p>• 增强软件服务子系统集：为 HarmonyOS 提供针对不同设备的、差异化的能力增强型软件服务，由智慧屏专有业务、穿戴专有业务、IoT 专有业务等子系统组成。</p><p>• 硬件服务子系统集：为 HarmonyOS 提供硬件服务，由位置服务、生物特征识别、穿戴专有硬件服务、IoT 专有硬件服务等子系统组成。</p><p>根据不同设备形态的部署环境，基础软件服务子系统集、增强软件服务子系统集、硬件服务子系统集内部可以按子系统粒度裁剪，每个子系统内部又可以按功能粒度裁剪。</p><p>2.3 框架层</p><p>框架层为 HarmonyOS 的应用程序提供了 Java/C/C++/JS 等多语言的用户程序框架和Ability 框架，以及各种软硬件服务对外开放的多语言框架 API；同时为采用 HarmonyOS的设备提供了 C/C++/JS 等多语言的框架 API，不同设备支持的 API 与系统的组件化裁剪程度相关。</p><p>2.4 应用层</p><p>应用层包括系统应用和第三方非系统应用。HarmonyOS 的应用由一个或多个 FA（Feature Ability）或 PA（Particle Ability）组成。其中，FA 有 UI 界面，提供与用户交互的能力；而 PA 无 UI 界面，提供后台运行任务的能力以及统一的数据访问抽象。基于FA/PA 开发的应用，能够实现特定的业务功能，支持跨设备调度与分发，为用户提供一致、高效的应用体验。</p><p><br/></p><p><br/></p>', '新加坡', '2022-06-07 00:44:23', 1, 1, 1, 1);
INSERT INTO `article` VALUES (116, 60, '前端', 'JavaScript', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">01_hello.html</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//alert是JavaScript语言提供的一个警告框函数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//它可以接受任意类型的参数，这个参数就是警告框的提示信息\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;hello&nbsp;world!&quot;)\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;!--\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;scr属性专门用来引入js文件路径（可以是相对路径，也可以是绝对路径）\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;script标签可以用来定义js代码，也可以用来引入js文件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，两个功能二选一使用，不能同时使用两个功能（用两个标签就行，按标签顺序执行）&nbsp;&nbsp;&nbsp;&nbsp;--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&nbsp;src=&quot;1.js&quot;&gt;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;```02_变量```javascript&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;i;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i&nbsp;=&nbsp;12;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//alert(&nbsp;typeof&nbsp;(i));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i&nbsp;=&nbsp;&quot;abc&quot;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//alert(typeof&nbsp;(i));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=12;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&quot;abc&quot;;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;a&nbsp;*&nbsp;b);&nbsp;//&nbsp;NaN是非数字，非数值\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">03_关系运算</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;&quot;12&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&nbsp;12;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(a&nbsp;==&nbsp;b);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(a&nbsp;===&nbsp;b);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;等于：&nbsp;==&nbsp;等于是简单的做字面值的比较\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;全等于：&nbsp;===&nbsp;除了做字面值的比较之外，还会比较两个变量的数据类型\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">04_逻辑运算</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在&nbsp;JavaScript&nbsp;语言中，所有的变量，都可以做为一个&nbsp;boolean&nbsp;类型的变量去使用。\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;0&nbsp;、null、&nbsp;undefined、””(空串)&nbsp;都认为是&nbsp;false；\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;0;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(a){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;零为真&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;零为假&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&nbsp;null;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(b){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;null为真&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;null为假&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;c&nbsp;=&nbsp;undefined;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(c){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;undefined为真&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;undefined为假&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;d&nbsp;=&nbsp;&quot;&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(d){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;空串为真&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;空串为假&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*&nbsp;&amp;&amp;&nbsp;且运算。\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有两种情况：\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第一种：当表达式全为真的时候。返回最后一个表达式的值。\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第二种：当表达式中，有一个为假的时候。返回第一个为假的表达式的值\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;&quot;abc&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;c&nbsp;=&nbsp;null;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;d&nbsp;=&nbsp;false;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;a&nbsp;&amp;&amp;&nbsp;b);//true\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;b&nbsp;&amp;&amp;&nbsp;a);//abc\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;a&nbsp;&amp;&amp;&nbsp;d);//false\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;a&nbsp;&amp;&amp;&nbsp;c);//null\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*&nbsp;||&nbsp;或运算\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第一种情况：当表达式全为假时，返回最后一个表达式的值\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;第二种情况：只要有一个表达式为真。就会把回第一个为真的表达式的值\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(d&nbsp;||&nbsp;c);//null\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(c&nbsp;||&nbsp;d);//false\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(a&nbsp;||&nbsp;b);//abc\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(a&nbsp;||&nbsp;c);//abc\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(b&nbsp;||&nbsp;c);//true\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">05_数组</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;arr&nbsp;=&nbsp;[];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arr.length);//0\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;arr[0]&nbsp;=&nbsp;12;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arr[0]);&nbsp;//与JAVA的区别&nbsp;：&nbsp;不会出现下标越界的错误\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arr.length);//1&nbsp;长度发生变化\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//JavaScript语言中的数组，只要我们通过数组下标赋值，那么最大的下标值，就会自动的给数组扩容操作\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;arr[2]&nbsp;=&nbsp;&quot;abc&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arr.length);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;arr.length;&nbsp;i++)\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arr[i]);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">06_函数<br/>第一种定义方式：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;使用的格式如下:&nbsp;function&nbsp;函数名(形参列表){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;函数体\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在&nbsp;JavaScript&nbsp;语言中，如何定义带有返回值的函数？\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只需要在函数体内直接使用&nbsp;return&nbsp;语句返回值即可！\n*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;定义一个无参函数\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;fun(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;无参函数fun（）被调用了&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//函数调用\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fun();\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;fun2(a,b){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;有参函数fun2（）被调用了&nbsp;a=&gt;&quot;+&nbsp;a&nbsp;+&nbsp;&quot;,b=&gt;&quot;&nbsp;+&nbsp;b);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;a&nbsp;=&nbsp;1;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;b&nbsp;=&nbsp;&quot;abc&quot;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fun2(a,b);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;sum(num1,num2){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;result&nbsp;=&nbsp;num1&nbsp;+&nbsp;num2;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(sum(360,160));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">第二种定义方式：</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;fun&nbsp;=&nbsp;function(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;无参函数&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fun();\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;fun2&nbsp;=&nbsp;function&nbsp;(a,b){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;有参函数a=&quot;&nbsp;+&nbsp;a&nbsp;+&nbsp;&quot;,b=&quot;&nbsp;+&nbsp;b);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fun2(1,2);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;fun3&nbsp;=&nbsp;function&nbsp;(num1,num2){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;num1&nbsp;+&nbsp;num2;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(fun3(160,360));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">07_隐型参数arguments</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;fun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//alert(&nbsp;arguments.length&nbsp;);//查看参数个数\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arguments[0]);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arguments[1]);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arguments[2]);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;arguments.length;&nbsp;i++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(arguments[i]);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;无参函数fun()&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*fun(1,&quot;ad&quot;,true);*/\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//需求：要求&nbsp;编写一个函数，用于计算所有参数相加的和并返回\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;sum(num1,num2)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;result&nbsp;=&nbsp;0;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;arguments.length;&nbsp;i++){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(typeof&nbsp;(arguments[i])&nbsp;==&nbsp;&quot;number&quot;)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;result&nbsp;+=&nbsp;arguments[i];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;result;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&nbsp;sum(1,2,3,4,5,6,7,8,9));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fun(1,&quot;ad&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">在 Java 中函数允许重载。但是在 JS 中函数的重载会直接覆盖掉上一次的定义</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">08_对象</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">Object 形式的自定义对象<br/>对象的定义： var 变量名 = new Object(); // 对象实例（空对象）<br/>变量名.属性名 = 值; // 定义一个属性<br/>变量名.函数名 = function(){} // 定义一个函数<br/>对象的访问： 变量名.属性 / 函数名();</p></blockquote><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;\n&nbsp;&lt;html&nbsp;lang=&quot;en&quot;&gt;&nbsp;\n&nbsp;&lt;head&gt;&nbsp;\n&nbsp;	&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;&nbsp;\n&nbsp;	&lt;title&gt;Title&lt;/title&gt;&nbsp;\n&nbsp;	&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;&nbsp;\n\n		//&nbsp;对象的定义：&nbsp;\n		//&nbsp;var&nbsp;变量名&nbsp;=&nbsp;new&nbsp;Object();&nbsp;//&nbsp;对象实例（空对象）&nbsp;\n		//&nbsp;变量名.属性名&nbsp;=&nbsp;值;&nbsp;//&nbsp;定义一个属性&nbsp;\n		//&nbsp;变量名.函数名&nbsp;=&nbsp;function(){}&nbsp;//&nbsp;定义一个函数&nbsp;\n		var&nbsp;obj&nbsp;=&nbsp;new&nbsp;Object();&nbsp;\n		obj.name&nbsp;=&nbsp;&quot;乐培&quot;;&nbsp;\n		obj.age&nbsp;=&nbsp;20;&nbsp;\n		obj.fun&nbsp;=&nbsp;function&nbsp;()&nbsp;{&nbsp;alert(&quot;姓名：&quot;&nbsp;+&nbsp;this.name&nbsp;+&nbsp;&quot;&nbsp;,&nbsp;年龄：&quot;&nbsp;+&nbsp;this.age);\n		&nbsp;}\n		&nbsp;//&nbsp;对象的访问：&nbsp;\n		&nbsp;//&nbsp;变量名.属性&nbsp;/&nbsp;函数名();&nbsp;\n		&nbsp;//&nbsp;alert(&nbsp;obj.age&nbsp;);&nbsp;\n		&nbsp;obj.fun();\n	&nbsp;&lt;/script&gt;&nbsp;&lt;/head&gt;&nbsp;&lt;body&gt;&nbsp;&lt;/body&gt;&nbsp;&lt;/html&gt;</pre><p><br/></p>', '印度', '2022-06-07 00:48:58', 0, 0, 2, 1);
INSERT INTO `article` VALUES (117, 60, '前端', 'JS事件', '<h2 style=\"box-sizing: border-box; margin: 0px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\">JS事件</h2><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>onload事件</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//onload事件的方法\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onloadfun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;静态注册onload事件，所有代码&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//onload事件动态注册、是固定写法\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;动态注册的onload事件&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;!--静态注册onload事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;onload事件是浏览器解析完页面之后就会自动触发的事件--&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>onclick事件</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onclickfun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;静态注册onclick事件&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态注册onclick事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//1、获取标签对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;document&nbsp;是&nbsp;JavaScript语言提供的一个对象&nbsp;（文档）\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;btnObj&nbsp;=&nbsp;document.getElementById(&quot;btn01&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//alert(btnObj);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//2、通过标签对象.事件名&nbsp;=&nbsp;function(){}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;btnObj.onclick&nbsp;=&nbsp;function&nbsp;(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;动态注册的onclick事件&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;&lt;!--静态注册onclick事件--&gt;\n&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;onclickfun();&quot;&gt;btn1&lt;/button&gt;\n&nbsp;&nbsp;&lt;button&nbsp;id=&quot;btn01&quot;&gt;btn2&lt;/button&gt;&lt;/body&gt;&lt;/html&gt;</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>onblur事件</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;乔布斯学JAVAWEB&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//静态注册onblur事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onblurFun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//console是控制台对象，是由JavaScript语言提供，专门用来向浏览器的控制器打印输出，用于测试使用\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&quot;静态注册失去焦点事件&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态注册onblur事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//1、获取标签对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;password&nbsp;=&nbsp;document.getElementById(&quot;password&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(password);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//2、通过标签对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;password.onblur&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;console.log(&quot;动态注册失去焦点事件&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;用户名：&lt;input&nbsp;type=&quot;text&quot;&nbsp;onblur=&quot;onblurFun()&quot;&gt;&lt;br&gt;\n&nbsp;&nbsp;密码：&lt;input&nbsp;id=&quot;password&quot;&nbsp;type=&quot;text&quot;&gt;&lt;br&gt;&lt;/body&gt;&lt;/html&gt;</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>onchange事件</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;下拉列表onchange&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onchangFun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;女神已经改变了&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态注册onchange事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;selObj&nbsp;=&nbsp;document.getElementById(&quot;chang1&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(selObj);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;selObj.onchange&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;男神已经改变了&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;请选择你心中的女神：&nbsp;&nbsp;&lt;!--静态注册onchange事件--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;select&nbsp;onchange=&quot;onchangFun();&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--张柏芝--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--宋祖儿--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--唐嫣--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--江疏影--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--迪丽热巴--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/select&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;\n&nbsp;&nbsp;请选择你心中的男神：&nbsp;&nbsp;&lt;!--动态注册onchange事件--&gt;\n&nbsp;&nbsp;&lt;select&nbsp;id=&quot;chang1&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--伟培--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--乐培--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--乐乐乐乐培--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--培哥--&lt;/option&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&gt;--培帅--&lt;/option&gt;\n&nbsp;&nbsp;&lt;/select&gt;&lt;/body&gt;&lt;/html&gt;</pre><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>onsubmit事件</h3><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;表单提交事件&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onsubmitFun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;要验证所有表单项是否合法，如果，有一个不合法就阻止表单提交\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;静态注册表单提交事件----发现不合法&quot;);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态注册onsubmit事件\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;subObj&nbsp;=&nbsp;document.getElementById(&quot;form01&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;subObj.onsubmit&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(&quot;动态注册表单提交时间-----发现不合法&quot;);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;&lt;!--return&nbsp;false&nbsp;可以阻止&nbsp;表单提交--&gt;\n&nbsp;&nbsp;&lt;form&nbsp;action=&quot;http://localhost:8080&quot;&nbsp;method=&quot;get&quot;&nbsp;onsubmit=&quot;return&nbsp;onsubmitFun();&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;submit&quot;&nbsp;value=&quot;静态注册&quot;&gt;\n&nbsp;&nbsp;&lt;/form&gt;\n\n&nbsp;&nbsp;&lt;form&nbsp;action=&quot;http://localhost:8080&quot;&nbsp;method=&quot;get&quot;&nbsp;id=&quot;form01&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;submit&quot;&nbsp;value=&quot;静态注册&quot;&gt;\n&nbsp;&nbsp;&lt;/form&gt;&lt;/body&gt;&lt;/html&gt;</pre><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">function Function的关系：<br/>function 是关键字，Function 是引用类型。<br/>使用function创建的变量是一个指针，指向的对象是一个Function对象。</p></blockquote><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>DOM模型</h2><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">DOM对象<br/>Document 对象的理解：<br/>第一点：Document 它管理了所有的 HTML 文档内容。<br/>第二点：document 它是一种树结构的文档。有层级关系。<br/>第三点：它让我们把所有的标签 都 对象化<br/>第四点：我们可以通过 document 访问所有的标签对象。</p></blockquote><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">注：<br/>document 对象的三个查询方法，如果有 id 属性，优先使用 getElementById 方法来进行查询<br/>如果没有 id 属性，则优先使用 getElementsByName 方法来进行查询<br/>如果 id 属性和 name 属性都没有最后再按标签名查 getElementsByTagName</p><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">以上三个方法，一定要在页面加载完成之后执行，才能查询到标签对象。</pre><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>getElementById.html</h4><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;需求：当用户点击了校验按钮，要获取输入框里的内容，然后验证其是否合法，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;验证的规则是：必须由字母，数字，下划线组成，并且长度是5到12位\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;onclickFun()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//1、当我们要操作一个标签时，一定要先获取这个标签对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;usernameObj&nbsp;=&nbsp;document.getElementById(&quot;username&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(usernameObj);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(usernameObj.value);/!*value是输入框内的实时内容*!/*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;usernameText&nbsp;=&nbsp;usernameObj.value;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//如何&nbsp;验证&nbsp;字符串，&nbsp;符合某个规则，&nbsp;需要使用正则表达式技术\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;patt&nbsp;=&nbsp;/^\\w{5,12}$/;/*得到的patt是一个对象*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;&nbsp;test()方法用于测试某个字符串,是不是匹配我的规则，\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;匹配就返回true，不匹配就返回false.\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;*/\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;usernameSpanObj&nbsp;=&nbsp;document.getElementById(&quot;usernameSpan&quot;);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;innerHTML&nbsp;表示起始标签和结束标签中的内容\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;innerHTML&nbsp;这个属性可读、可写\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(patt.test(usernameText))&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;usernameSpanObj.innerHTML&nbsp;=&nbsp;&quot;&lt;img&nbsp;src=\\&quot;right.png\\&quot;&nbsp;width=\\&quot;18\\&quot;&nbsp;height=\\&quot;18\\&quot;&gt;&quot;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;usernameSpanObj.innerHTML&nbsp;=&nbsp;&quot;&lt;img&nbsp;src=\\&quot;wrong.png\\&quot;&nbsp;width=\\&quot;18\\&quot;&nbsp;height=\\&quot;18\\&quot;&gt;&quot;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;用户名：&lt;input&nbsp;type=&quot;text&quot;&nbsp;id=&quot;username&quot;&nbsp;value=&quot;lwp&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;span&nbsp;id=&quot;usernameSpan&quot;&nbsp;style=&quot;color:red&quot;&gt;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/span&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;onclickFun()&quot;&gt;校验&lt;/button&gt;&lt;/body&gt;&lt;/html&gt;</pre><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>getElementsByname.html</h4><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//全选\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;checkAll()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;让所有复选框都选中\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//document.getElementsByName();是根据&nbsp;指定的name属性查询返回多个标签对象集合\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//这个集合的操作跟数组一样\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//集合中每个元素都是dom对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//这个集合中的元素顺序是他们html页面中从上到下的顺序\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;hobbies&nbsp;=&nbsp;document.getElementsByName(&quot;hobby&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(hobbies[1].value);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//checked表示复选框的选中状态，选中true，否则false\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;hobbies.length;&nbsp;i++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hobbies[i].checked&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//全不选\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;checkNo()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;hobbies&nbsp;=&nbsp;document.getElementsByName(&quot;hobby&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;hobbies.length;&nbsp;i++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hobbies[i].checked&nbsp;=&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//反选\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;checkReverse()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;hobbies&nbsp;=&nbsp;document.getElementsByName(&quot;hobby&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;hobbies.length;&nbsp;i++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hobbies[i].checked&nbsp;=&nbsp;!hobbies[i].checked;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*if&nbsp;(hobbies[i].checked)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hobbies[i].checked&nbsp;=&nbsp;false;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hobbies[i].checked&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;兴趣爱好：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;cpp&quot;&gt;C++\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;java&quot;&gt;Java&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;js&quot;&gt;JavaScript&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;checkAll()&quot;&gt;全选&lt;/button&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;checkNo()&quot;&gt;全不选&lt;/button&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;checkReverse()&quot;&gt;反选&lt;/button&gt;&lt;/body&gt;&lt;/html&gt;</pre><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>getElementsByTagName.html</h4><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;checkAll()&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//document.getElementsByTagName(&quot;input&quot;);是按照指定标签名来进行查询并返回集合\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//这个集合的操作跟数组一样\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//集合中都是dom对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//集合中元素顺序&nbsp;是他们在html页面中从上到下的顺序\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;inputs&nbsp;=&nbsp;document.getElementsByTagName(&quot;input&quot;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(inputs);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;inputs.length;&nbsp;i++)&nbsp;{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;inputs[i].checked&nbsp;=&nbsp;true;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;兴趣爱好：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;cpp&quot;&gt;C++\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;java&quot;&gt;Java&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;checkbox&quot;&nbsp;name=&quot;hobby&quot;&nbsp;value=&quot;js&quot;&gt;JavaScript&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;button&nbsp;onclick=&quot;checkAll()&quot;&gt;全选&lt;/button&gt;&lt;/body&gt;&lt;/html&gt;</pre><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>regexp.html（正则表达式）</h4><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;乔布斯学正则表达式&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求字符串中，是否包含字母e\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;new&nbsp;RegExp(&quot;e&quot;);*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/e/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求字符串中，是否包含字母a或b或c\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/[abc]/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求字符串，是否包含小写字母\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/[a-z]/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求字符串，是否包含大写字母\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/[A-Z]/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求字符串，是否包含任意数字\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/[0-9]/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求字符串，是否包含任意字母，数字和下划线，是个“或”的关系\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/\\w/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求&nbsp;字符串中是否包含至少一个a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a+/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求&nbsp;字符串中是否包含零个&nbsp;或&nbsp;多个a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a*!/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;表示要求&nbsp;字符串是否包含一个或零个a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a?/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求&nbsp;字符串是否包含连续三个a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a{3}/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求&nbsp;字符串是否包含&nbsp;至少3个连续的a，最多5个连续的a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a{3,5}/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求&nbsp;字符串是否&nbsp;包含&nbsp;至少3个连续的a\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a{3,}/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求&nbsp;字符串必须以a结尾\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/a$/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//表示要求&nbsp;字符串必须以a开头\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/^a/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//从头到尾匹配3到5个a，完全匹配！\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*var&nbsp;patt&nbsp;=&nbsp;/^a{3,5}$/;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;patt&nbsp;=&nbsp;/^\\w{5,12}$/;//从头到尾匹配5到12个包含数字、字母、下划线的字符串\n\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*alert(patt);*/\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;str&nbsp;=&nbsp;&quot;aa&quot;;\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(patt.test(str));\n\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><h4 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 26px; color: rgb(79, 79, 79); font-size: 18px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>createElement.html</h4><pre style=\"box-sizing: border-box; font-family: &quot;Source Code Pro&quot;, &quot;DejaVu Sans Mono&quot;, &quot;Ubuntu Mono&quot;, &quot;Anonymous Pro&quot;, &quot;Droid Sans Mono&quot;, Menlo, Monaco, Consolas, Inconsolata, Courier, monospace, &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; margin-top: 1.2em; margin-bottom: 24px; line-height: 22px; color: rgb(0, 0, 0);\">&lt;!DOCTYPE&nbsp;html&gt;&lt;html&nbsp;lang=&quot;en&quot;&gt;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;UTF-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Title&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;window.onload&nbsp;=&nbsp;function&nbsp;()&nbsp;{\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//在内存中&lt;div&gt;&lt;/div&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;divObj&nbsp;=&nbsp;document.createElement(&quot;div&quot;);\n\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;textNodeObj&nbsp;=&nbsp;document.createTextNode(&quot;乐培真的好帅！&quot;);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;divObj.appendChild(textNodeObj);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*divObj.innerHTML&nbsp;=&nbsp;&quot;乐培好帅&quot;&nbsp;;*/\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.body.appendChild(divObj);\n\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;&lt;/head&gt;&lt;body&gt;&lt;/body&gt;&lt;/html&gt;</pre><p><br/></p>', '英国', '2022-06-07 00:53:30', 0, 0, 1, 1);
INSERT INTO `article` VALUES (118, 60, '人工智能', '指令周期，时序产生器和控制方式', '<h1 style=\"box-sizing: border-box; font-size: 24px; margin: 8px 0px 16px; line-height: 32px; color: rgb(79, 79, 79);\">指令周期</h1><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>指令周期的基本概念</h2><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">CPU执行程序是一个“取指令–执行指令”的循环过程</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">指令周期：<br/>CPU从内存中取出一条指令，<span style=\"box-sizing: border-box; font-weight: 700;\">并执行完毕</span>的时间总和。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">CPU周期：<br/>又称机器周期，一般为<span style=\"box-sizing: border-box; font-weight: 700;\">从内存读取一条指令字的最短时间</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">时钟周期：<br/>也叫节拍脉冲或T周期，是计算机工作的基本时间单位。</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><img src=\"https://img-blog.csdnimg.cn/20210509214255214.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">如上图所示，指令周期通常包含CPU周期，而CPU周期又由若干个时钟周期形成。<br/>需要说明的是，不同的计算机系统中定义的术语未必相同。例如，在不采用三级时序的系统中，机器周期就相当于时钟周期。</p></blockquote><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>5条典型的指令</h2><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">RR型指令（寄存器寄存器）：不需要访问内存，访问寄存器的指令格式<br/>SS型指令（存储器存储器）：涉及内存单元，机器执行指令需要多次访问内存<br/>RS型指令（寄存器-存储器）：执行此类指令时，既要访问内存单元，又要访问寄存器</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1、MOV指令：<br/>MOV是一条<span style=\"box-sizing: border-box; font-weight: 700;\">RR型</span>指令，它需要两个CPU周期，其中取指周期需要一个CPU周期，执行周期需要一个CPU周期。<br/><img src=\"https://img-blog.csdnimg.cn/20210509215546189.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/><br/>2、LAD指令：<br/>LAD指令是RS型指令，由于一次访问指存(指令存储器)，一次访问数存(数据存储器)，LAD指令的指令周期需要3个CPU周期。<br/><img src=\"https://img-blog.csdnimg.cn/20210509215817327.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/></p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">取下条指令的操作时 PC+1（程序计数器自增）</p></blockquote><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">3、ADD指令：<br/>ADD指令是RR型指令，指令周期只需两个CPU周期</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">4、STO指令：<br/>STO指令是RS型指令，因此指令周期需3个CPU周期</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">5、JMP指令：<br/>JMP指令是一条<span style=\"box-sizing: border-box; font-weight: 700;\">无条件转移指令</span>，用来改变程序的执行顺序。指令周期为两个CPU周期</p><h1 style=\"box-sizing: border-box; font-size: 24px; margin: 8px 0px 16px; line-height: 32px; color: rgb(79, 79, 79);\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>时序产生器和控制方式</h1><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>时序信号的作用和体制</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">时序信号的作用：<br/>使计算机准确、迅速‘有条不紊地工作；</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">CPU通过时序控制来识别指令和数据：<br/><span style=\"box-sizing: border-box; font-weight: 700;\">指令周期的第一个CPU周期——取指周期</span><br/>·从存储单元中取出的一定是指令，需要送往IR指令寄存器；<br/><span style=\"box-sizing: border-box; font-weight: 700;\">指令周期的其他CPU周期——执行周期</span><br/>·从存储单元中取出的一定是数据，需要送往运算器</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">时序信号的体制：电位——脉冲制</p><blockquote style=\"box-sizing: border-box; margin: 0px 0px 24px; color: rgba(0, 0, 0, 0.5); padding: 16px; border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; word-break: break-word !important;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; font-size: 14px; color: rgb(85, 86, 102); line-height: 22px; overflow: auto hidden;\">脉冲到达之前，电平信号必须要稳定</p></blockquote><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>控制器的时序信号</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\"><span style=\"box-sizing: border-box; font-weight: 700;\">硬布线控制器的时序信号</span><br/>常采用&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">主状态周期—节拍电位—节拍脉冲</span>&nbsp;三级体制。<br/><span style=\"box-sizing: border-box; font-weight: 700;\">微程序控制器的时序信号</span><br/>一般采用&nbsp;<span style=\"box-sizing: border-box; font-weight: 700;\">节拍电位—节拍脉冲</span>&nbsp;二级体制<br/><img src=\"https://img-blog.csdnimg.cn/20210509221803706.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3FxXzQ2MTU2MzI4,size_16,color_FFFFFF,t_70\" alt=\"在这里插入图片描述\"/></p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>时序信号产生器</h2><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">定义：用逻辑电路实现时序的器件</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>微程序控制器中时序信号产生器的组成部分</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1、时钟源：<br/>提供稳定的基本方波时钟脉冲信号</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">2、环形脉冲发生器：<br/>产生一组有序的间隔相等或不等的脉冲序列</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">3、节拍脉冲和读写时序译码逻辑<br/>环形脉冲与节拍脉冲共同作用产生各控制信号的时序</p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">4、启停控制逻辑</p><h2 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); font-size: 22px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>控制方式</h2><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>同步控制方式</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">在任何情况下，已定的指令在执行时所需的机器周期数和时钟周期数都<span style=\"box-sizing: border-box; font-weight: 700;\">固定不变</span></p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>异步控制方式</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">每个指令周期包含的机器周期数目<span style=\"box-sizing: border-box; font-weight: 700;\">由联络信号控制</span>，不固定</p><h3 style=\"box-sizing: border-box; margin: 8px 0px 16px; line-height: 28px; color: rgb(79, 79, 79); font-size: 20px;\"><a style=\"box-sizing: border-box; background-color: transparent; color: rgb(78, 161, 219);\"></a>联合控制方式</h3><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); line-height: 26px; overflow: auto hidden;\">1、<span style=\"box-sizing: border-box; font-weight: 700;\">大部分控制序列为固定的机器周期</span>，对某些时间难以确定的操作则以执行部件的“回答”信号作为本次操作的结束。<br/>2、<span style=\"box-sizing: border-box; font-weight: 700;\">机器周期的节拍脉冲数固定</span>，但是各条指令周期的机器周期数不固定。</p><p><br/></p>', '英国', '2022-06-07 00:54:31', 1, 0, 1, 1);
INSERT INTO `article` VALUES (119, 44, '前端', 'Gitee+PicGo搭建个人免费图床', '<p>### picgo是目前我用到的最好用的图片上传工具，图片可以上传到阿里云、腾讯云等云服务器，还可以上传到github、gitee等代码托管平台，上传成功后会自动复制链接或markdown格式或html格式到剪切板。下面介绍使用picgo+gitee快速搭建个人免费图床的方法。</p><p><br/></p><p># 一、下载并安装PicGo以及相关配置</p><p><br/></p><p>1、官网下载picgo，官网地址：[https://github.com/Molunerfinn/PicGo/releases](https://github.com/Molunerfinn/PicGo/releases)</p><p><br/></p><p>如果官网无法打开可以下载我下载好的安装包：[https://www.aliyundrive.com/s/v2pV1LALPQR](https://www.aliyundrive.com/s/v2pV1LALPQR)</p><p><br/></p><p>我使用的是Windows版本，直接下载exe文件即可，其他操作系统可自行选择对应的版本。</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721210949.png&quot; alt=&quot;image-20210721210948944&quot; style=&quot;zoom:80%;&quot; /&gt;</p><p><br/></p><p>2、picgo安装，选择安装目录默认安装即可</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721211740.png&quot; alt=&quot;image-20210721211733521&quot; style=&quot;zoom:67%;&quot; /&gt;</p><p><br/></p><p>3、picgo设置</p><p><br/></p><p>picgo默认有以下图片存储方式，但是除了github外几乎所有的都要小钱钱（……-……），而且众所周知github又经常无法访问，所以我们就想可不可以使用码云代替，不错，我们确实可以使用码云代替github，只需下载对应的插件即可。</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721211927.png&quot; alt=&quot;image-20210721211927622&quot; style=&quot;zoom:80%;&quot; /&gt;</p><p><br/></p><p>插件下载：找到插件设置，搜索gitee，选择其中任意一个即可，我选择的是gitee-uploader 1.1.2版本，点击安装</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721212257.png&quot; alt=&quot;image-20210721212257404&quot; style=&quot;zoom:80%;&quot; /&gt;</p><p><br/></p><p>安装完成后图床设置中就会出现gitee选项</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721212518.png&quot; alt=&quot;image-20210721212518720&quot; style=&quot;zoom:80%;&quot; /&gt;</p><p><br/></p><p>gitee设置：repo是你要存放图片的仓库，创建方式下面会列出，branch是仓库中图片要上传到的分支(注意一定要初始化分支，否则会上传失败)，token是确认你个人身份的私人密钥，path是图片存放的路径。custom尽量不要改。</p><p><br/></p><p>repo中填写仓库名称，示例如下，token填写新生成的私人令牌，初始化分支master即可。</p><p><br/></p><p>&lt;img src=&quot;https://gitee.com/weiyuexin/blog-images/raw/master/img/20210721212659.png&quot; alt=&quot;image-20210721212658972&quot; style=&quot;zoom:80%;&quot; /&gt;</p><p><br/></p><p>## 二、配置gitee仓库</p><p><br/></p><p>1、创建gitee仓库，仓库设置为开源。</p><p><br/></p><p>![image-20210721213803184](https://img-blog.csdnimg.cn/img_convert/ea7613267ec15c1343c89a619b3ea7c8.png)</p><p><br/></p><p>2、创建完成后计入设置-安全设置-私人令牌-生成新令牌，勾选project，点击提交，密码验证后生成新令牌，复制令牌，粘贴到picgo的gitee设置中的token选项中即可。</p><p><br/></p><p>![image-20210721214003399](https://img-blog.csdnimg.cn/img_convert/efdc1d7466e435342f4105bc5bff3860.png)</p><p><br/></p><p>![image-20210721214046911](https://img-blog.csdnimg.cn/img_convert/0e5d34dabfecd47cf66a574df01608d5.png)</p><p><br/></p><p>![image-20210721214209360](https://img-blog.csdnimg.cn/img_convert/ebac0a4c34ad445175f460daac3dd9e5.png)</p><p><br/></p><p>![image-20210721214303350](https://img-blog.csdnimg.cn/img_convert/ec8f97bc452ba1c6e9de88137d960aaa.png)</p><p><br/></p><p>![image-20210721214402632](https://img-blog.csdnimg.cn/img_convert/053dd65c30f511db5f85d3ad2fcc587a.png)</p><p><br/></p><p>3、全部配置完成后选择图片上传测试。</p><p><br/></p><p>Windows提示上传成功</p><p><br/></p><p>![image-20210721215057158](https://img-blog.csdnimg.cn/img_convert/7fa0e0561b3e52e41407c8ab11be887c.png)</p><p><br/></p><p>查看gitee仓库，看到图片已上传并可通过链接访问。</p><p><br/></p><p>![image-20210721215140305](https://img-blog.csdnimg.cn/img_convert/30fccd7168cca5df425dc138447145d1.png)</p><p><br/></p><p>## 三、配置Typora的图片上传实现自动上传图片</p><p><br/></p><p>选择文件-偏好设置-图像，插入图片时选择上传图片，勾选以下四项，上传服务选择picgo，选择picgo安装路径即可。然后在使用Typora时粘贴图片就会自动将图片上传到码云并将图片链接改成码云图片链接。这样就可以快乐的使用Typora编辑Markdown文档了。</p><p><br/></p><p>![image-20210721215716024](https://img-blog.csdnimg.cn/img_convert/5e19198ad4d32a2f4eb5fca072a8d10a.png)</p><p><br/></p><p>![image-20210721220009791](https://img-blog.csdnimg.cn/img_convert/c3a02e9fe1a1d9a9355042079adadb3f.png)</p><p><br/></p>', '河北', '2022-06-07 01:01:51', 0, 0, 1, 1);
INSERT INTO `article` VALUES (120, 44, 'Cpp', '数据结构实验三——栈和队列的基本操作', '<p><br/></p><p>@[TOC](栈和队列)</p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p># 一、实验内容</p><p><br/></p><p>***&nbsp;</p><p><br/></p><p>### 1.实验目的</p><p><br/></p><p>***</p><p>&nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>&nbsp;1. 编程实现顺序栈和链栈的基本操作：建栈，取栈顶元素，入栈，出栈；</p><p>&nbsp;2. 编程实现循环队列和链队列的基本操作：建队列，取队头元素，入队，出队。</p><p><br/></p><p>&nbsp;***</p><p><br/></p><p>### 2. 基本要求</p><p><br/></p><p>&nbsp;***</p><p>&nbsp;</p><p><br/></p><p>&nbsp;1. 掌握栈的顺序存储结构、链式存储结构及其基本操作；</p><p>&nbsp;2. 掌握队列的顺序存储结构、链式存储结构及其基本操作。</p><p><br/></p><p>&nbsp;***</p><p><br/></p><p>### 3. 支撑的课程目标</p><p><br/></p><p>***</p><p><br/></p><p>本实验项目可以支撑“目标1. 理解数据结构的基本概念、计算机内部数据对象的表示和特性。掌握线性表、树、图等数据逻辑结构、存储结构及其差异以及各种操作的实现。”、和“目标2能够针对实际问题选择合适的数据结构和方法设计出结构清晰、正确易读、复杂性较优的算法，同时掌握对算法进行时间、空间复杂度分析的基本技能。”。</p><p>本实验通过验证方式引导学生掌握顺序栈和链栈的基本操作，循环队列和链队列的基本<span style=\"white-space:pre\">	</span>操作，为后续学习打下基础，以便更好地达成后续更高层次的课程目标。</p><p>***</p><p><br/></p><p># 二、实验过程</p><p><br/></p><p>***</p><p><br/></p><p>&nbsp;***</p><p><br/></p><p>### 1. 顺序栈的建栈、入栈、出栈、取栈顶元素</p><p><br/></p><p>***</p><p>&nbsp;</p><p><br/></p><p>```cpp</p><p>/*</p><p>*title:顺序栈的基本操作</p><p>*writer:weiyuexin</p><p>*data:2020-10-11</p><p>*/</p><p><br/></p><p>#include&lt;iostream&gt;</p><p><br/></p><p>using namespace std;</p><p><br/></p><p>#define SElemType int</p><p><br/></p><p>typedef struct{</p><p>&nbsp; &nbsp; SElemType *base;&nbsp; &nbsp; //定义栈底指针</p><p>&nbsp; &nbsp; SElemType *top;&nbsp; &nbsp; &nbsp;//定义栈顶指针</p><p>&nbsp; &nbsp; int stacksize;</p><p>}SqStack;</p><p><br/></p><p>void InitStack(SqStack &amp;S,int maxsize){&nbsp; &nbsp; //初始化栈</p><p>&nbsp; &nbsp; S.base = new SElemType[maxsize];</p><p>&nbsp; &nbsp; if(!S.base){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;创建栈失败!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; S.top = S.base;&nbsp; &nbsp; &nbsp;//top初始化为base,是空栈</p><p>&nbsp; &nbsp; S.stacksize = maxsize;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;成功创建了一个最大容量为&quot;&lt;&lt;S.stacksize&lt;&lt;&quot;的顺序栈!&quot;&lt;&lt;endl;</p><p>}</p><p><br/></p><p>void Display(SqStack S){&nbsp; &nbsp; &nbsp;//打印顺序栈</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;该顺序栈为:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;栈底=&gt;&quot;;</p><p>&nbsp; &nbsp; for(int i=0;i&lt;S.stacksize;i++){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;*S.base++&lt;&lt;&quot; &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if(!*S.base || S.top == S.base){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;&lt;=栈顶&quot;&lt;&lt;endl;</p><p>}</p><p><br/></p><p>void Push(SqStack &amp;S,SElemType e){&nbsp; &nbsp; //入栈</p><p>&nbsp; &nbsp; if(S.top - S.base == S.stacksize){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;栈已满，无法插入!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; *S.top++=e;</p><p>}</p><p><br/></p><p>SElemType Pop(SqStack &amp;S){&nbsp; &nbsp; //出栈</p><p>&nbsp; &nbsp; if(S.top == S.base){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;栈空!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; SElemType e;</p><p>&nbsp; &nbsp; e = *--S.top;</p><p>&nbsp; &nbsp; cout&lt;&lt;e&lt;&lt;&quot; &quot;;</p><p>}</p><p><br/></p><p>SElemType GetTop(SqStack S){</p><p>&nbsp; &nbsp; if(S.top != S.base){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return *(S.top-1);</p><p>&nbsp; &nbsp; }</p><p>}</p><p><br/></p><p>int main(){</p><p>&nbsp; &nbsp; SqStack S;</p><p>&nbsp; &nbsp; int maxsize;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;实验3_1-------------------顺序栈的基本操作&quot;&lt;&lt;endl&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;1------------------建栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;2------------------入栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;3------------------出栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;4------------------打印顺序栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;5------------------取栈顶元素&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;0------------------结束程序运行&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; int operation;</p><p>&nbsp; &nbsp; while(cin&gt;&gt;operation){</p><p><br/></p><p>&nbsp; &nbsp; &nbsp; &nbsp; switch(operation){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 0 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;程序运行结束!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return 0;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 1 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入栈的最大容量:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cin&gt;&gt;maxsize;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; InitStack(S,maxsize);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 2 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入数据元素,当输入-1时结束:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; int e;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; while(cin&gt;&gt;e){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(e == -1){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Push(S,e);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;入栈成功!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 3 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入出栈元素的个数:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; int m;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cin&gt;&gt;m;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;出栈的元素为:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(int i= 0;i&lt;m;i++){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Pop(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;(先出-&gt;后出)&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 4 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 5 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;栈顶元素为: &quot;&lt;&lt;GetTop(S)&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; default :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;您选择的操作不存在，请重新选择:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; return 0;</p><p>}</p><p>```</p><p><br/></p><p>&nbsp;</p><p>### 1. 链栈的建栈、入栈、出栈、及取栈顶元素</p><p><br/></p><p><br/></p><p><br/></p><p>```cpp</p><p>/*</p><p>*title:链栈的基本操作</p><p>*writer:weiyuexin</p><p>*data:2020-10-11</p><p>*/</p><p><br/></p><p>#include&lt;iostream&gt;</p><p><br/></p><p>using namespace std;</p><p><br/></p><p>#define ElemType int</p><p><br/></p><p>typedef struct StackNode{</p><p>&nbsp; &nbsp; ElemType data;</p><p>&nbsp; &nbsp; struct StackNode *next;</p><p>}StackNode,*LinkStack;</p><p><br/></p><p>void InitStack(LinkStack &amp;S){&nbsp; &nbsp; &nbsp;//初始化链栈</p><p>&nbsp; &nbsp; S = NULL;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;栈S初始化成功!&quot;&lt;&lt;endl;</p><p>}</p><p><br/></p><p>void Display(LinkStack S){</p><p>&nbsp; &nbsp; LinkStack p = S;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;该链栈为:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;栈顶=&gt; &quot;;</p><p>&nbsp; &nbsp; while(p){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;p-&gt;data&lt;&lt;&quot; &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; p = p-&gt;next;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot; &lt;=栈底&quot;&lt;&lt;endl;</p><p>}</p><p><br/></p><p>ElemType Pop(LinkStack &amp;S,ElemType ee){</p><p>&nbsp; &nbsp; if(S == NULL){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;栈已空，无法在进行删除操作!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 0;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; StackNode *p = new StackNode();</p><p>&nbsp; &nbsp; ee = S-&gt;data;</p><p>&nbsp; &nbsp; p = S;</p><p>&nbsp; &nbsp; S = S-&gt;next;</p><p>&nbsp; &nbsp; delete p;</p><p>&nbsp; &nbsp; return ee;</p><p>}</p><p><br/></p><p>void Push(LinkStack &amp;S,ElemType e){&nbsp; &nbsp; &nbsp;//入栈</p><p>&nbsp; &nbsp; StackNode *p = new StackNode();</p><p>&nbsp; &nbsp; p -&gt;data = e;</p><p>&nbsp; &nbsp; p -&gt;next = S;</p><p>&nbsp; &nbsp; S = p;</p><p>}</p><p><br/></p><p>int main(){</p><p>&nbsp; &nbsp; LinkStack S;</p><p><br/></p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;实验3_2-------------------链栈的基本操作&quot;&lt;&lt;endl&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;1------------------初始化链栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;2------------------入栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;3------------------出栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;4------------------打印链栈&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;5------------------取栈顶元素&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;0------------------结束程序运行&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; int operation;</p><p>&nbsp; &nbsp; while(cin&gt;&gt;operation){</p><p><br/></p><p>&nbsp; &nbsp; &nbsp; &nbsp; switch(operation){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 0 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;程序运行结束!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return 0;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 1 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; InitStack(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 2 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入数据元素,当输入-1时结束:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ElemType e;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; while(cin&gt;&gt;e){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(e == -1){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Push(S,e);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;入栈成功!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 3 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入出栈元素的个数:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; int n;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cin&gt;&gt;n;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;出栈的元素依次为: &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(int i=0;i&lt;n;i++){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ElemType ee;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;Pop(S,ee)&lt;&lt;&quot; &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 4 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(S);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 5 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;栈顶元素为:&quot;&lt;&lt;S-&gt;data&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; default :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;您选择的操作不存在，请重新选择:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应数字选择操作：&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; return 0;</p><p>}</p><p>```</p><p>***</p><p><br/></p><p>### 3.循环队列的建立、入队、出队及取队头元素</p><p><br/></p><p>***</p><p>```cpp</p><p>/*</p><p>*title: 循环队列的基本操作</p><p>*writer:weiyuexin</p><p>*data:2020-10-11</p><p>*/</p><p><br/></p><p>#include&lt;iostream&gt;</p><p><br/></p><p>using namespace std;</p><p><br/></p><p>#define MAXSIZE 100</p><p>#define QElemType int</p><p><br/></p><p>typedef struct{</p><p>&nbsp; &nbsp; QElemType *base;&nbsp; &nbsp; &nbsp;//存储空间基地址</p><p>&nbsp; &nbsp; int front;&nbsp; &nbsp; //头指针</p><p>&nbsp; &nbsp; int rear;&nbsp; &nbsp; &nbsp;//尾指针</p><p>}SqQueue;</p><p><br/></p><p>void InitQueue(SqQueue &amp;Q){&nbsp; &nbsp; &nbsp; &nbsp; //初始化循环队列</p><p>&nbsp; &nbsp; Q.base = new QElemType[MAXSIZE];</p><p>&nbsp; &nbsp; if(!Q.base){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;队列初始化失败!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; Q.front = Q.rear = 0;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;循环队列初始化成功!&quot;&lt;&lt;endl;</p><p>}</p><p><br/></p><p>void EnQueue(SqQueue &amp;Q,QElemType e){&nbsp; &nbsp; //入队</p><p>&nbsp; &nbsp; if((Q.rear + 1)%MAXSIZE == Q.front){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;该队列已满无法插入!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; Q.base[Q.rear] = e;&nbsp; &nbsp; //新元素插入对尾</p><p>&nbsp; &nbsp; Q.rear = (Q.rear + 1)%MAXSIZE;&nbsp; &nbsp; &nbsp; //队尾指针加一</p><p>}</p><p><br/></p><p>QElemType DeQueue(SqQueue &amp;Q,QElemType &amp;ee){</p><p>&nbsp; &nbsp; if(Q.front == Q.rear){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;该循环队列已空!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 0;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; ee = Q.base[Q.front];&nbsp; &nbsp;//保存队首指针</p><p>&nbsp; &nbsp; Q.front = (Q.front + 1)%MAXSIZE;&nbsp; &nbsp; //队首指针加一</p><p>&nbsp; &nbsp; return ee;</p><p>}</p><p><br/></p><p>void Display(SqQueue Q){&nbsp; &nbsp; &nbsp;//打印循环队列</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;该循环队列为: &quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; cout&lt;&lt;&quot;队首=&gt;&quot;;</p><p>&nbsp; &nbsp; for(int i=Q.front;i&lt;MAXSIZE;i++){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if(!Q.base[i]){</p><p>&nbsp;