sp; &nbsp; QElemType e;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cin&gt;&gt;e;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; EnQueue(Q,e);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;n&lt;&lt;&quot;个元素入队成功!&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(Q);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应的数字选择您想要进行的操作:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 3 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入要出栈的元素的个数:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; int m;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cin&gt;&gt;m;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;出栈的元素依次为: &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for(int i=0;i&lt;m;i++){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;DeQueue(Q)&lt;&lt;&quot; &quot;;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(Q);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应的数字选择您想要进行的操作:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 4 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;该链队队首值为: &quot;&lt;&lt;GetHead(Q)&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应的数字选择您想要进行的操作:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; case 5 :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Display(Q);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;请输入对应的数字选择您想要进行的操作:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; default :</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; cout&lt;&lt;&quot;您选择的操作不存在，请重新选择:&quot;&lt;&lt;endl;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; return 0;</p><p>}</p><p><br/></p><p>```</p><p><br/></p>', '河北', '2022-06-07 01:02:39', 0, 1, 2, 1);
INSERT INTO `article` VALUES (121, 60, '系统运维', '系统设计基础', '<h1 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 2.5rem; margin: 2em 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 2.75rem; letter-spacing: -1.5px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain md-expand\" style=\"box-sizing: border-box;\">系统设计基础</span></h1><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-comment\" style=\"box-sizing: border-box; color: rgb(90, 149, 227); opacity: 0.8; font-family: var(--monospace);\">&lt;!-- GFM-TOC --&gt;</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"list-style-type: square;\"><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E7%B3%BB%E7%BB%9F%E8%AE%BE%E8%AE%A1%E5%9F%BA%E7%A1%80\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">系统设计基础</span></a></span></p></li><ul class=\"custom_dash list-paddingleft-1\" style=\"list-style-type: dash;\"><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E4%B8%80%E6%80%A7%E8%83%BD\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">一、性能</span></a></span></p></li><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E4%BA%8C%E4%BC%B8%E7%BC%A9%E6%80%A7\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">二、伸缩性</span></a></span></p></li><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E4%B8%89%E6%89%A9%E5%B1%95%E6%80%A7\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">三、扩展性</span></a></span></p></li><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E5%9B%9B%E5%8F%AF%E7%94%A8%E6%80%A7\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">四、可用性</span></a></span></p></li><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E4%BA%94%E5%AE%89%E5%85%A8%E6%80%A7\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">五、安全性</span></a></span></p></li><li class=\"list-dash list-dash-paddingleft\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-meta-i-c  md-link\" style=\"box-sizing: border-box;\"><a class=\"md-inner-link\" href=\"#%E5%8F%82%E8%80%83%E8%B5%84%E6%96%99\" style=\"box-sizing: border-box; cursor: pointer; outline: 0px; transition: all 0.2s ease-in-out 0s; color: rgb(224, 224, 224); -webkit-user-drag: none;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">参考资料</span></a></span><span class=\"md-softbreak\" style=\"box-sizing: border-box;\"></span><span class=\"md-comment\" style=\"box-sizing: border-box; color: rgb(90, 149, 227); opacity: 0.8; font-family: var(--monospace);\">&lt;!-- GFM-TOC --&gt;</span></p></li></ul></ul><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">一、性能</span></h2><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">性能指标</span></h3><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">1. 响应时间</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">指某个请求从发出到接收到响应消耗的时间。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">在对响应时间进行测试时，通常采用重复请求的方式，然后计算平均响应时间。</span></p><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">2. 吞吐量</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">指系统在单位时间内可以处理的请求数量，通常使用每秒的请求数来衡量。</span></p><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">3. 并发用户数</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">指系统能同时处理的并发用户请求数量。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">在没有并发存在的系统中，请求被顺序执行，此时响应时间为吞吐量的倒数。例如系统支持的吞吐量为 100 req/s，那么平均响应时间应该为 0.01s。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">目前的大型系统都支持多线程来处理并发请求，多线程能够提高吞吐量以及缩短响应时间，主要有两个原因：</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"list-style-type: square;\"><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">多 CPU</span></p></li><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">IO 等待时间</span></p></li></ul><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">使用 IO 多路复用等方式，系统在等待一个 IO 操作完成的这段时间内不需要被阻塞，可以去处理其它请求。通过将这个等待时间利用起来，使得 CPU 利用率大大提高。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">并发用户数不是越高越好，因为如果并发用户数太高，系统来不及处理这么多的请求，会使得过多的请求需要等待，那么响应时间就会大大提高。</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">性能优化</span></h3><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">1. 集群</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">将多台服务器组成集群，使用负载均衡将请求转发到集群中，避免单一服务器的负载压力过大导致性能降低。</span></p><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">2. 缓存</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">缓存能够提高性能的原因如下：</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"list-style-type: square;\"><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">缓存数据通常位于内存等介质中，这种介质对于读操作特别快；</span></p></li><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">缓存数据可以位于靠近用户的地理位置上；</span></p></li><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">可以将计算结果进行缓存，从而避免重复计算。</span></p></li></ul><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.12rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; font-weight: normal; clear: both; overflow-wrap: break-word; padding: 0px; color: white; line-height: 1.375rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">3. 异步</span></h4><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">某些流程可以将操作转换为消息，将消息发送到消息队列之后立即返回，之后这个操作会被异步处理。</span></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">二、伸缩性</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">指不断向集群中添加服务器来缓解不断上升的用户并发访问压力和不断增长的数据存储需求。</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">伸缩性与性能</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">如果系统存在性能问题，那么单个用户的请求总是很慢的；</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">如果系统存在伸缩性问题，那么单个用户的请求可能会很快，但是在并发数很高的情况下系统会很慢。</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">实现伸缩性</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">应用服务器只要不具有状态，那么就可以很容易地通过负载均衡器向集群中添加新的服务器。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">关系型数据库的伸缩性通过 Sharding 来实现，将数据按一定的规则分布到不同的节点上，从而解决单台存储服务器的存储空间限制。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">对于非关系型数据库，它们天生就是为海量数据而诞生，对伸缩性的支持特别好。</span></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">三、扩展性</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">指的是添加新功能时对现有系统的其它应用无影响，这就要求不同应用具备低耦合的特点。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">实现可扩展主要有两种方式：</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"list-style-type: square;\"><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">使用消息队列进行解耦，应用之间通过消息传递进行通信；</span></p></li><li class=\"md-\" style=\"\"><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">使用分布式服务将业务和可复用的服务分离开来，业务使用分布式服务框架调用可复用的服务。新增的产品可以通过调用可复用的服务来实现业务逻辑，对其它产品没有影响。</span></p></li></ul><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">四、可用性</span></h2><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">冗余</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">保证高可用的主要手段是使用冗余，当某个服务器故障时就请求其它服务器。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">应用服务器的冗余比较容易实现，只要保证应用服务器不具有状态，那么某个应用服务器故障时，负载均衡器将该应用服务器原先的用户请求转发到另一个应用服务器上，不会对用户有任何影响。</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">存储服务器的冗余需要使用主从复制来实现，当主服务器故障时，需要提升从服务器为主服务器，这个过程称为切换。</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">监控</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">对 CPU、内存、磁盘、网络等系统负载信息进行监控，当某个信息达到一定阈值时通知运维人员，从而在系统发生故障之前及时发现问题。</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.17rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.5rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">服务降级</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">服务降级是系统为了应对大量的请求，主动关闭部分功能，从而保证核心功能可用。</span></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">五、安全性</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 1.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative; color: rgb(184, 191, 198); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;Segoe UI Emoji&quot;, sans-serif;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">要求系统在应对各种攻击手段时能够有可靠的应对措施。</span></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; orphans: 4; font-size: 1.63rem; margin: 0px 0px 1.5rem; font-family: &quot;Lucida Grande&quot;, Corbel, sans-serif; clear: both; overflow-wrap: break-word; padding: 0px; color: rgb(222, 222, 222); line-height: 1.875rem; letter-spacing: -1px; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">参考资料</span></h2><ul class=\"ul-list list-paddingleft-2\" style=\"list-style-type: square;\"><li class=\"md- md-focus-container\" style=\"\"><p class=\"md-end-block md-p md-focus\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; overflow-wrap: break-word; white-space: pre-wrap; position: relative;\"><span class=\"md-plain md-expand\" style=\"box-sizing: border-box;\">大型网站技术架构：核心原理与案例分析</span></p></li></ul><p><br/></p>', '西班牙', '2022-06-07 01:04:22', 0, 0, 0, 1);
